 String queryGetSLAConfig = "SELECT * FROM app_fd_valores_sla WHERE c_nome like ? ";
 if(urgente.equals("SIM")){
 queryGetSLAConfig += "AND c_nome like '%URGENTE'";
 paramOperativa += "%";
 }
 
 stmt = con.prepareStatement(queryGetSLAConfig);
 stmt.setObject(1, paramOperativa);
 rs = stmt.executeQuery();
 String sla = ""; 
 if(rs.next()){
 sla = rs.getObject("c_sla");
 }
 
 
 
 String queryCreateSLA = "INSERT INTO app_fd_sla_atividade (id, dateCreated, dateModified, createdBy,createdByName ,modifiedBy, modifiedByName, c_data_inicio, c_data_fim, c_nome_atividade, c_id_processo, c_utilizador_atribuido, c_estado_atividade) VALUES (?,?,?,?,?,?,?,?,?,?,?,?,?);";
 stmt = con.prepareStatement(queryCreateSLA);
 
 var idSLA = UuidGenerator.getInstance().getUuid();
 var dateNow ="#date.yyyy-MM-dd HH:mm#";
 var currentUsername = "#currentUser.username#";
 var currentNameUser = "#currentUser.firstName#" + "#currentUser.lastName#";
 String dataFim = calculateSLAD(dateNow, holidays, sla, urgente);
 
 var estadoAtividade = "Em curso";
 stmt.setObject(1, idSLA);
 stmt.setObject(2, dateNow);
 stmt.setObject(3, dateNow);
 stmt.setObject(4, currentUsername);
 stmt.setObject(5, currentNameUser);
 stmt.setObject(6, currentUsername);
 stmt.setObject(7, currentNameUser);
 stmt.setObject(8, dateNow);
 stmt.setObject(9, dataFim); 
 stmt.setObject(10, nomeAtividade);
 stmt.setObject(11, ProcessID);
 stmt.setObject(12, utilizador);
 stmt.setObject(13, estadoAtividade);
 
 rs = stmt.executeQuery();
 
 }
 }
 
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error getting info from DB");
 }finally{
 try{
 if(rs != null){
 rs.close();
 }
 if(stmt != null){
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 }
 
 }
}
updateSLA();
wm.processVariable(ProcessID, "operativa_anterior" , "CONTRATACAO");
wm.processVariable(ProcessID, "id_processo_anterior" , ProcessID);


