 String username = "#currentUser.username#";
 
 
 //getCurrentThreadUser() Method used by system to get current thread user
 String currentUser = wum.getCurrentThreadUser();
 wum.setCurrentThreadUser(replaceUser);
 wum.setCurrentThreadUser(currentUser);
 
 //Reassigns the assignment from a user (replaceUser) to another user (username)
 wm.assignmentReassign("", "", aid, username, replaceUser);
 
