public void verificarDataOutorga() {
 String processId = "#process.processId#";
 String recordId = "#process.recordId#";
 
 //Set the workflow variable
 ExtDirectoryManager directoryManager = (ExtDirectoryManager) pluginManager.getBean("directoryManager");
 WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
 wm.processVariable(processId, "flag_ultima_atividade" , "0");
 
 String currentDate = "#date.yyyy-MM-dd hh:mm#";
 String dataOutorga = "";
 String horaOutorga = "";
 String cedulaEntidade = "";
 String usernameEntidade = "";
 String entidadeDepId = "";
 String entidadeEmail = "";
 
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 
 try {
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 
 if(!con.isClosed()) {
 if(recordId != null) {
 
 //Atualizar fase e subfase do processo
 String updateFaseProcesso = "UPDATE app_fd_detalhe_processo SET c_fase_processo = ? WHERE c_id_processo = ?";
 stmt = con.prepareStatement(updateFaseProcesso);
 stmt.setObject(1, "AO");
 stmt.setObject(2, recordId);
 rs = stmt.executeQuery();
 
 String updateSubfaseProcesso = "UPDATE app_fd_detalhe_processo SET c_subfase_processo = ? WHERE c_id_processo = ?";
 stmt = con.prepareStatement(updateSubfaseProcesso);
 stmt.setObject(1, "A Aguardar Distribuio");
 stmt.setObject(2, recordId);
 rs = stmt.executeQuery();
 
 try {
 SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm");
 
 String horaDistribuicao = "#date.yyyy-MM-dd#" + " 17:00"; /** AVALIAR SOLUO PARA NO SER HARDCODED **/
 
 Date dtCurrentDate = simpleDateFormat.parse(currentDate);
 Date dtHoraDistribuicao = simpleDateFormat.parse(horaDistribuicao);
 
 if(dtCurrentDate.after(dtHoraDistribuicao)) {
 String queryObterDataOutorga = "SELECT c_dataOutorga, c_horaOutorga FROM app_fd_atos a WHERE c_id_processo LIKE ? AND c_status LIKE 'AGD';";
 stmt = con.prepareStatement(queryObterDataOutorga);
 stmt.setObject(1, recordId);
 rs = stmt.executeQuery();
 
 if (rs.next()) {
 dataOutorga = rs.getObject("c_dataOutorga");
 horaOutorga = rs.getObject("c_horaOutorga");
 }
 
 String urgencia = "";
 
 if (!(dataOutorga.isEmpty() || horaOutorga.isEmpty())) { 
 String dataHoraOutorga = dataOutorga + " " + horaOutorga;
 Date dtDataHoraOutorga = simpleDateFormat.parse(dataHoraOutorga);
 
 long diffInMillies = Math.abs(dtDataHoraOutorga.getTime() - dtCurrentDate.getTime());
 long diff = TimeUnit.DAYS.convert(diffInMillies, TimeUnit.MILLISECONDS);
 
 if (diff < 1) {
 urgencia = "1";
 
 } else{
 urgencia = "0";
 }
 } else {
 urgencia = "0";
 }
 
 String idDetalheProcesso = "#form.processos.detalhe_processo#";
 
 // Guardar urgncia do processo na BD
 String updateUrgenciaProcesso = "UPDATE app_fd_detalhe_processo SET c_urgente_ao = ? WHERE id LIKE ?;";
 stmt = con.prepareStatement(updateUrgenciaProcesso);
 stmt.setObject(1, urgencia);
 stmt.setObject(2, idDetalheProcesso);
 rs = stmt.executeQuery();
 }
 
 } catch (ParseException e) {
 e.printStackTrace();
 }
 
 
 // Obter Cdula da Entidade Autenticadora para o Ato Agendado
 String queryObterCedulaEntidade = "SELECT c_cedula_entidade FROM app_fd_atos a WHERE c_id_processo LIKE ? AND c_status LIKE 'AGD';";
 stmt = con.prepareStatement(queryObterCedulaEntidade);
 stmt.setObject(1, recordId);
 rs = stmt.executeQuery();
 
 if (rs.next()) {
 cedulaEntidade = rs.getObject("c_cedula_entidade");
 }
 
 // Se encontrar cdula no ato agendado
 if (!cedulaEntidade.isEmpty()) {
 
 // Obter Username da Entidade com esta Cdula
 String queryObterUsernameEntidade = "SELECT c_user_colaborador, c_cedula_profissional, c_departamentos, c_email_profissional FROM app_fd_info_colaboradores c WHERE c_cedula_profissional LIKE ? AND c_departamentos LIKE 'PL_EXT_ENT%';";
 stmt = con.prepareStatement(queryObterUsernameEntidade);
 stmt.setObject(1, cedulaEntidade);
 rs = stmt.executeQuery();
 
 if (rs.next()) {
 usernameEntidade = rs.getObject("c_user_colaborador");
 entidadeDepId = rs.getObject("c_departamentos");
 entidadeEmail = rs.getObject("c_email_profissional");
 }
 }
 
 // Se encontrar username da Entidade
 if (!(usernameEntidade.isEmpty() || entidadeEmail.isEmpty())) {
 
 // Guarda Username da Entidade na Wf Variable
 wm.processVariable(processId, "nome_entidade", usernameEntidade);
 
 // Guarda Email da Entidade na Wf Variable
 wm.processVariable(processId, "email_entidade", entidadeEmail);
 
 // Define o Tipo de Entidade consoante o seu departamento
 if (entidadeDepId.contains("PL_EXT_ENT_A")) {
 wm.processVariable(processId, "tipo_entidade", "AUT");
 }
 
 if (entidadeDepId.contains("PL_EXT_ENT_SA")) {
 wm.processVariable(processId, "tipo_entidade", "SEMI");
 }
 
 if (entidadeDepId.contains("PL_EXT_ENT_NA")) {
 wm.processVariable(processId, "tipo_entidade", "NAUT");
 }
 } else {
 wm.processVariable(processId, "tipo_entidade", "NAUT");
 wm.processVariable(processId, "email_entidade", "tsimao@neyond.pt");
 }
 }
 }
 
 } catch(Exception e) {
 LogUtil.error("AO | Verificar Data de Outorga", e, "Error getting info from DB");
 } finally {
 try {
 if(rs != null) {
 rs.close();
 }
 if(stmt != null) {
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 } catch(Exception e) {
 LogUtil.error("AO | Verificar Data de Outorga", e, "Error closing DB connection");
 }
 }
}


