String ProcessID = workflowAssignment.getProcessId();

//Set the workflow variable
WorkflowManager wm = (WorkflowManager) pluginManager.getBean("workflowManager");
wm.processVariable(ProcessID, "flag_ultima_atividade" , "0");
wm.processVariable(ProcessID, "proxima_operativa" , "");
