public void checkValidadeCertidao(Connection con, PreparedStatement stmt, ResultSet rs, ResultSet rs2){

 String idDetalhe = "";
 String banco = "";
 String processId = "";
 String nProcesso = "";
 String diligenciaId = null;
 String processId = null;
 String resourceId = null;


 String query = "SELECT p.id as 'IDProcesso', dp.id as 'IdDetalhe', dp.c_n_processo as 'NProc', dp.c_validade_certidao, dp.c_banco_fundo as 'Banco' FROM app_fd_processos_procu p LEFT JOIN app_fd_detalhe_proce_procu dp ON dp.id = p.c_detalhe_processo AND dp.c_flag_notificacao_certidao != 'SIM' WHERE dp.c_validade_certidao != '' AND dp.c_validade_certidao <= DATE_SUB(CURDATE(), INTERVAL -30 DAY);";
 stmt = con.prepareStatement(query); 

 rs = stmt.executeQuery();

 while(rs.next()){
 processId = rs.getObject("IDProcesso");
 nProcesso = rs.getObject("NProc");
 banco = rs.getObject("Banco");
 idDetalhe = rs.getObject("IdDetalhe");

 String equipaDest = "SISTEMA";
 String equipaRem = "PL_SIS_PROCU";
 String gestorRem = "procuradoria_geral";


 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", gestorRem);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );


 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligncias");

 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);

 diligenciaId = result.getProcess().getInstanceId();


 String activityIdQuery = "select ass.activityID, ass.ResourceID from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
 stmt = con.prepareStatement(activityIdQuery);
 stmt.setObject(1, diligenciaId);
 rs2 = stmt.executeQuery();

 if(rs2.next()){

 workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs2.getString(1), rs2.getString(2));

 String assunto = "Alerta | Validade da Certido | Processo - " + nProcesso;
 String mensagem = "A validade da certido do processo " + nProcesso + " caduca a 30 ou menos dias.";
 String insertQuery = "INSERT INTO app_fd_info_diligencia(id, c_id_processo, c_operativa_destinataria, c_equipa_destinataria, c_gestor_destinatario, c_operativa_remetente, c_equipa_remetente, c_gestor_remetente, c_assunto, c_tipo_diligencia, c_diligencia, c_urgencia, c_banco, c_complexidade, dateCreated) VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)ON DUPLICATE KEY UPDATE id = VALUES(id), c_id_processo = VALUES(c_id_processo), c_operativa_destinataria= VALUES(c_operativa_destinataria), c_equipa_destinataria = VALUES(c_equipa_destinataria), c_gestor_destinatario = VALUES(c_gestor_destinatario), c_operativa_remetente = VALUES(c_operativa_remetente), c_equipa_remetente = VALUES(c_equipa_remetente), c_gestor_remetente = VALUES(c_gestor_remetente), c_assunto = VALUES(c_assunto), c_tipo_diligencia = VALUES(c_tipo_diligencia), c_diligencia = VALUES(c_diligencia), c_urgencia = VALUES(c_urgencia), c_banco= VALUES(c_banco), c_complexidade= VALUES(c_complexidade), dateCreated= VALUES(dateCreated);";

 stmt = con.prepareStatement(insertQuery);
 stmt.setObject(1, diligenciaId);
 stmt.setObject(2, processId);
 stmt.setObject(3, "PROCU");
 stmt.setObject(4, equipaDest);
 stmt.setObject(5, "SISTEMA");
 stmt.setObject(6, "PROCU");
 stmt.setObject(7, equipaRem);
 stmt.setObject(8, gestorRem);
 stmt.setObject(9, assunto);
 stmt.setObject(10, "ALERTA");
 stmt.setObject(11, mensagem);
 stmt.setObject(12, "SIM");
 stmt.setObject(13, banco);
 stmt.setObject(14, "NOR");
 stmt.setObject(15, "#date.yyyy-MM-dd hh:mm:ss#");
 stmt.executeQuery();
 LogUtil.info("Procuradoria - Notificao Validade da Certido", "Foi enviada uma diligncia automtica relativa ao processo " + processId);
 }

 //Atualizar flag de notificacao para SIM
 String queryUpdateCertidaoFlag = "UPDATE app_fd_detalhe_proce_procu SET c_flag_notificacao_certidao = 'SIM' WHERE id = ?";
 stmt = con.prepareStatement(queryUpdateCertidaoFlag);
 stmt.setObject(1, idDetalhe);
 stmt.executeQuery();
 }

}

public void checkAlertaGestor(Connection con, PreparedStatement stmt, ResultSet rs, ResultSet rs2){

 String queryGetAlertas = "SELECT p.id as 'IDProcesso', dp.c_alerta_gestor as 'AlertaGestor' , dp.c_n_agenda as 'NProc', dp.c_user_alerta as 'UserAlerta', dp.id as 'IdDetalhe', dp.c_banco_fundo as 'Banco' FROM app_fd_processos_procu p LEFT JOIN app_fd_detalhe_proce_procu dp ON dp.id = p.c_detalhe_processo AND c_alerta_gestor = 'SIM' WHERE dp.c_data_alerta = CURDATE()";
 stmt = con.prepareStatement(queryGetAlertas);
 rs = stmt.executeQuery();

 String processId = null;
 String banco = null;
 String nProc = null;
 String userAlerta = null;
 String complexidade = null;
 String faseProcesso = null;
 String diligenciaId = null;
 String idDetalhe = null;

 while(rs.next()){

 processId = rs.getObject("IDProcesso");
 idDetalhe = rs.getObject("IdDetalhe");
 banco = rs.getObject("Banco");
 nProc= rs.getObject("NProc");
 userAlerta = rs.getObject("UserAlerta");

 String equipaDest = "SISTEMA";
 String equipaRem = "PL_INT_PROCU";

 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", userAlerta);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );

 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligncias");
 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
 diligenciaId = result.getProcess().getInstanceId();

 String queryGetActivity = "select ass.activityID as 'ActivityId', ass.ResourceID as 'ResourceId' from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
 stmt = con.prepareStatement(queryGetActivity);
 stmt.setObject(1, diligenciaId);
 rs2 = stmt.executeQuery();

 if(rs2.next()){
 //Avanar dilig~encia uma fase
 workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs2.getObject("ActivityId"), rs2.getObject("ResourceId"));

 //Inserir registo de Diligncia
 String assunto = "Alerta | Gestor | Processo - " + nProc;
 String mensagem = "Existe um alerta no processo " + nProc ;
 String insertQuery = "INSERT INTO app_fd_info_diligencia(id, c_id_processo, c_operativa_destinataria, c_equipa_destinataria, c_gestor_destinatario, c_operativa_remetente, c_equipa_remetente, c_gestor_remetente, c_assunto, c_tipo_diligencia, c_diligencia, c_urgencia, c_banco, c_complexidade, dateCreated) VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?) ON DUPLICATE KEY UPDATE id = VALUES(id), c_id_processo = VALUES(c_id_processo), c_operativa_destinataria= VALUES(c_operativa_destinataria), c_equipa_destinataria = VALUES(c_equipa_destinataria), c_gestor_destinatario = VALUES(c_gestor_destinatario), c_operativa_remetente = VALUES(c_operativa_remetente), c_equipa_remetente = VALUES(c_equipa_remetente), c_gestor_remetente = VALUES(c_gestor_remetente), c_assunto = VALUES(c_assunto), c_tipo_diligencia = VALUES(c_tipo_diligencia), c_diligencia = VALUES(c_diligencia), c_urgencia = VALUES(c_urgencia), c_banco= VALUES(c_banco), c_complexidade= VALUES(c_complexidade), dateCreated= VALUES(dateCreated);";
 stmt = con.prepareStatement(insertQuery);
 stmt.setObject(1, diligenciaId);
 stmt.setObject(2, processId);
 stmt.setObject(3, "PROCU");
 stmt.setObject(4, equipaDest);
 stmt.setObject(5, "SISTEMA");
 stmt.setObject(6, "PROCU");
 stmt.setObject(7, equipaRem);
 stmt.setObject(8, userAlerta);
 stmt.setObject(9, assunto);
 stmt.setObject(10, "ALERTA");
 stmt.setObject(11, mensagem);
 stmt.setObject(12, "SIM");
 stmt.setObject(13, banco);
 stmt.setObject(14, "NOR");
 stmt.setObject(15, "#date.yyyy-MM-dd hh:mm:ss#");
 stmt.executeQuery();

 LogUtil.info("Alerta Gestor", "Foi enviada uma diligncia automtica relativa ao processo " + processId);
 //Reset na flag de Alerta
 String queryResetFlagAlerta = "UPDATE app_fd_detalhe_proce_procu SET c_alerta_gestor = '', c_user_alerta = '' WHERE id= ?";
 stmt = con.prepareStatement(queryResetFlagAlerta);
 stmt.setObject(1, idDetalhe);
 stmt.executeQuery();

 }
 }
}

public void verificarAlteracoesAgenda(Connection con, PreparedStatement stmt, ResultSet rs, ResultSet rs2){

 String queryGetAtosAlterados = "SELECT ato.c_id_processo as 'processId', ato.c_id_agendamento as 'idAgendamento' ,dp.c_n_agenda as 'NAgenda', dp.c_banco_fundo as 'BancoFundo' FROM app_fd_atos ato JOIN (SELECT id, t.c_id_processo, dateCreated FROM (SELECT MAX(dateCreated) as max_date, a.c_id_processo FROM app_fd_atos a GROUP BY a.c_id_processo) r INNER JOIN app_fd_atos t ON r.c_id_processo = t.c_id_processo AND r.max_date = t.dateCreated) ato2 ON ato.id=ato2.id LEFT JOIN app_fd_processos_procu p ON p.id = ato.c_id_processo LEFT JOIN app_fd_detalhe_proce_procu dp ON dp.id = p.c_detalhe_processo JOIN app_fd_agendamento_procu a ON ato.c_id_agendamento = a.id AND a.c_flag_notificacao_alteracoes != 1 LEFT JOIN shkprocesses sp ON sp.ActivityRequesterProcessId = p.id LEFT JOIN shkprocessstates sps ON sps.oid = sp.State LEFT JOIN SHKActivities sact ON sact.ProcessId = sp.Id LEFT JOIN SHKActivityStates ssta ON ssta.oid = sact.State WHERE ato.c_flag_alteracoes_agenda = 1 AND sps.KeyValue LIKE 'open.running' AND p.id is not null AND (ssta.oid = '1000001' OR ssta.oid = '1000003' OR ssta.oid = '1000005') AND sp.id like '%PCDProcuradoria%'";
 stmt = con.prepareStatement(queryGetAtosAlterados);
 rs = stmt.executeQuery();

 String processId ="";
 String nAgenda = "";
 String bancoFundo = "";
 String idAgendamento = "";
 while(rs.next()){
 processId = rs.getObject("processId");
 nAgenda = rs.getObject("NAgenda");
 bancoFundo = rs.getObject("BancoFundo");
 idAgendamento = rs.getObject("idAgendamento");
 String equipaDest = "SISTEMA";
 String equipaRem = "PL_INT_PROCU";
 String userAlerta = "procuradoria_geral";

 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", userAlerta);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );
 variables.put("tipo_alerta","alteracaoAgendaProcu");

 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligncias");
 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
 diligenciaId = result.getProcess().getInstanceId();


 String queryGetActivity = "select ass.activityID as 'ActivityId', ass.ResourceID as 'ResourceId' from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
 stmt = con.prepareStatement(queryGetActivity);
 stmt.setObject(1, diligenciaId);
 rs2 = stmt.executeQuery();

 if(rs2.next()){
 //Avanar dilig~encia uma fase
 workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs2.getObject("ActivityId"), rs2.getObject("ResourceId"));

 //Inserir registo de Diligncia
 String assunto = "Notificao | Alteraes Agenda | Processo - " + nAgenda;
 String mensagem = "Houve alteraes pela agenda no processo: " + nAgenda ;
 String insertQuery = "INSERT INTO app_fd_info_diligencia(id, c_id_processo, c_operativa_destinataria, c_equipa_destinataria, c_gestor_destinatario, c_operativa_remetente, c_equipa_remetente, c_gestor_remetente, c_assunto, c_tipo_diligencia, c_diligencia, c_urgencia, c_banco, c_complexidade, dateCreated) VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?) ON DUPLICATE KEY UPDATE id = VALUES(id), c_id_processo = VALUES(c_id_processo), c_operativa_destinataria= VALUES(c_operativa_destinataria), c_equipa_destinataria = VALUES(c_equipa_destinataria), c_gestor_destinatario = VALUES(c_gestor_destinatario), c_operativa_remetente = VALUES(c_operativa_remetente), c_equipa_remetente = VALUES(c_equipa_remetente), c_gestor_remetente = VALUES(c_gestor_remetente), c_assunto = VALUES(c_assunto), c_tipo_diligencia = VALUES(c_tipo_diligencia), c_diligencia = VALUES(c_diligencia), c_urgencia = VALUES(c_urgencia), c_banco= VALUES(c_banco), c_complexidade= VALUES(c_complexidade), dateCreated= VALUES(dateCreated);";
 stmt = con.prepareStatement(insertQuery);
 stmt.setObject(1, diligenciaId);
 stmt.setObject(2, processId);
 stmt.setObject(3, "PROCU");
 stmt.setObject(4, equipaDest);
 stmt.setObject(5, "SISTEMA");
 stmt.setObject(6, "PROCU");
 stmt.setObject(7, equipaRem);
 stmt.setObject(8, userAlerta);
 stmt.setObject(9, assunto);
 stmt.setObject(10, "ALERTA");
 stmt.setObject(11, mensagem);
 stmt.setObject(12, "SIM");
 stmt.setObject(13, bancoFundo);
 stmt.setObject(14, "NOR");
 stmt.setObject(15, "#date.yyyy-MM-dd hh:mm:ss#");
 stmt.executeQuery();

 LogUtil.info("Alerta Gestor", "Foi enviada uma diligncia automtica de alteres pela agenda relativa ao processo " + processId);

 //Reset na flag de Alerta
 String queryResetFlagAlerta = "UPDATE app_fd_agendamento_procu SET c_flag_notificacao_alteracoes = '1' WHERE id= ?";
 stmt = con.prepareStatement(queryResetFlagAlerta);
 stmt.setObject(1, idAgendamento);
 stmt.executeQuery();

 }

 }

}


public void checkNotificacoes(){
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 ResultSet rs2 = null;

 try{
 DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 if(!con.isClosed()){
 checkValidadeCertidao(con, stmt, rs, rs2);
 checkAlertaGestor(con, stmt, rs, rs2);
 verificarAlteracoesAgenda(con, stmt, rs, rs2);
 }

 }catch(Exception e){
 LogUtil.error("Notificaes Procuradoria", e, "Error in Notificaes");
 }finally{
 try{
 if(rs != null){
 rs.close();
 }
 if(rs2 != null){
 rs2.close();
 }
 if(stmt != null){
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 }catch(Exception e){
 LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 }
 }
}




checkNotificacoes();"}

