{"script":"import org.joget.commons.util.LogUtil;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.model.AppDefinition;
import org.joget.workflow.model.service.WorkflowManager;
import org.joget.workflow.model.service.WorkflowUserManager;
import org.joget.workflow.model.WorkflowProcess;
import org.joget.workflow.model.WorkflowProcessResult;

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

public void envioAlertaContratacao(){

 AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
 WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
 AppDefinition appDef = AppUtil.getCurrentAppDefinition();


 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 String diligenciaId = null;

 try{
 DataSource ds = (DataSource) AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();

 if(!con.isClosed()){
 //varivel que indica se o processo passou por Produo Robot
 String producao_robot = "#variable.producao_robot#";
 String banco = "#variable.banco#";
 String complexidade = "#variable.complexidade#";
 String variableUserContName = "user_contratacao_" + banco;
 if(banco.equals("MTG")){
 variableUserContName += "2";
 }
 if(complexidade.equals("COM")){
 variableUserContName = variableUserContName + "_com"; 
 }

 String pid = "#process.recordId#";


 //Se o processo passou por Produo Robot
 if(producao_robot.trim().equals("1")){

 String queryGetProcesso = "SELECT dp.c_fase_processo as 'FaseProcesso', DP.c_n_wf_pl as 'NWFPL', sactdata.VariableValueVCHAR as 'UserContratacao' FROM app_fd_processos p LEFT JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo JOIN shkprocesses shk ON p.id = shk.ActivityRequesterProcessId JOIN SHKActivities sact ON sact.ProcessId = shk.Id JOIN shkactivitydata sactdata ON sact.oid = sactdata.activity WHERE p.id = ? AND sactdata.VariableDefinitionId = ? ORDER BY sact.oid DESC ";
 stmt = con.prepareStatement(queryGetProcesso);
 stmt.setObject(1, pid);
 stmt.setObject(2, variableUserContName);

 rs=stmt.executeQuery();

 if(rs.next()){
 String faseProcesso = rs.getObject("FaseProcesso");
 String equipaDest ="SISTEMA";
 String equipaRem = "PL_INT_CNT_" + banco + "_" + complexidade + "_CRH";
 String userContratacao = rs.getObject("UserContratacao");
 String nWfPl = rs.getObject("NWFPL");

 //if User Contratacao existir
 if(!userContratacao.isEmpty()){
 Map variables = new HashMap();
 variables.put("equipa_destinataria", equipaDest);
 variables.put("gestor_remetente", userContratacao);
 variables.put("gestor_destinatario", "SISTEMA");
 variables.put("equipa_remetente",equipaRem );
 variables.put("flag_alerta","1" );

 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligncias");

 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);

 diligenciaId = result.getProcess().getInstanceId();

 //Obter id da atividade da Diligncia para avanar uma fase
 String activityIdQuery = "select ass.activityID as 'ActivityId', ass.ResourceID as 'ResourceId' from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
 stmt = con.prepareStatement(activityIdQuery);
 stmt.setObject(1, diligenciaId);
 rs = stmt.executeQuery();
 if(rs.next()){
 workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs.getObject("ActivityId"), rs.getObject("ResourceId"));

