public void criarProcessoProcuradoria(){

 AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
 WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
 AppDefinition appDef = AppUtil.getCurrentAppDefinition();
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 try{

 String banco_fundo = "#form.detalhe_proce_dist.nome_banco[{form.processos_distrates.detalhe_processo[{variable.id_distrates}]}]#";
 String numeroProcesso = "#form.detalhe_proce_dist.num_pedido[{form.processos_distrates.detalhe_processo[{variable.id_distrates}]}]#";
 String tipoProcesso = "DIST";
 String dataEntrada = "#date.yyyy-MM-dd HH:mm#";
 String idDistrates = "#variable.id_distrates#";
 String ProcessId = workflowAssignment.getProcessId();
 /**Check if process exists**/
 boolean processExists = false;
 String processId = "";
 LogUtil.info("IdDistrates","AAA " + idDistrates);
 LogUtil.info("banco","AAA " + banco_fundo);
 LogUtil.info("nprocesso","AAA " + numeroProcesso);
 LogUtil.info("ProcessId","AAA " + ProcessId);
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();

 if(!con.isClosed()){
 String queryGetProcessId = "SELECT p.id FROM app_fd_processos_procu p JOIN app_fd_detalhe_proce_procu dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_processo = ? ;";
 stmt = con.prepareStatement(queryGetProcessId);
 stmt.setObject(1,numeroProcesso);
 rs = stmt.executeQuery();
 if(rs.next()){
 processId = rs.getObject("p.id");
 processExists=true;

 }

 //Se o processo no existir
 if(!processExists){
 //start process with variables
 Map variables = new HashMap();
 variables.put("id_distrates", idDistrates);
 //get process
 WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Procuradoria");
 //start process
 WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
 //get process id
 processId = result.getProcess().getInstanceId();
 //Atualizar varivel no processo de Distrates
 workflowManager.processVariable(ProcessId, "enviado_procuradoria" , "1");

 //String instanceId = AppUtil.processHashVariable("#process.activityInst." + activityDefId + ".instanceId["+subprocessId+"]#",null,null,null);
 //workflowManager.activityVariable(instanceId,"enviado_procuradoria" , "1");

 //definition of the fields of table processos where to store each generated primary key
 String[] fieldIds = {"detalhe_processo","comunicacoes","agendamento", "documentos", "conclusao"};
 //definition of the formIds that correspond to the pages of the multipage form
 String[] formIds = {"detalheProcessoProcuradoria", "comunicacoes", "agendamentoProcuradoria", "ficheirosProcuradoria", "conclusaoProcuradoria"};


 String fkDetalheProcesso = "";
 String fkComunicacoes ="";
 String fkAgendamento = "";
 String fkDocumentos = "";
 String fkConclusao = "";
 for(int i = 0; i < fieldIds.length; i++){
 String foreignKey = UuidGenerator.getInstance().getUuid();

 if(fieldIds[i].equals("detalhe_processo") ){
 fkDetalheProcesso = foreignKey;
 String queryInsert = "INSERT INTO app_fd_detalhe_proce_procu(id, c_banco_fundo, c_n_processo, c_tipo_processo, c_estado, c_data_primeira_entrada, c_data_entrada, c_id_processo) VALUES(?,?,?,?,?,?,?,?);";
 stmt = con.prepareStatement(queryInsert);
 stmt.setObject(1,foreignKey);
 stmt.setObject(2,banco_fundo);
 stmt.setObject(3,numeroProcesso);
