public Collection getValidadorInicial(WorkflowActivity activity) {
 // [TO DO] Obter qual a entidade autenticadora do processo
 
 Collection participantes = new ArrayList();
 ExtDirectoryManager directoryManager = (ExtDirectoryManager) pluginManager.getBean("directoryManager");
 WorkflowManager wm = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");

 String recordId = "#process.recordId#";
 String tipoEntidade = "#variable.tipo_entidade#";
 
 if (tipoEntidade.equals("AUT")) {
 participantes.add("#variable.nome_entidade#");
 }
 
 if (tipoEntidade.equals("NAUT") || tipoEntidade.equals("SEMI")) {
 String userDistribuicaoManual = "#variable.username_distribuicao_manual#";
 
 if (userDistribuicaoManual.isEmpty()) {
 Connection con = null;
 PreparedStatement stmt = null;
 ResultSet rs = null;
 
 try {
 DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
 con = ds.getConnection();
 
 if(!con.isClosed()) {
 String dataOutorga = "";
 String horaOutorga = "";
 String username = "";
 
 String queryObterDataOutorga = "SELECT c_dataOutorga, c_horaOutorga FROM app_fd_atos a WHERE c_id_processo LIKE ? AND c_status LIKE 'AGD';";
 stmt = con.prepareStatement(queryObterDataOutorga);
 stmt.setObject(1, recordId);
 rs = stmt.executeQuery();
 
 if (rs.next()) {
 dataOutorga = rs.getObject("c_dataOutorga");
 horaOutorga = rs.getObject("c_horaOutorga");
 }
 
 if (!(dataOutorga.isEmpty() || horaOutorga.isEmpty())) {
 // Obter Gestor com menos processos alocados
 String queryObterGestor = "SELECT c_user_colaborador, c_email_profissional, numero_processos, numero_processos_esp, estado, data_outorga, hora_outorga FROM app_fd_info_colaboradores col LEFT JOIN (SELECT ato.c_status AS estado, ato.c_dataOutorga AS data_outorga, ato.c_horaOutorga AS hora_outorga, p.id AS idProc, count(p.id) as numero_processos, (SELECT count(p.id) FROM shkprocesses sp LEFT JOIN shkprocesses sp2 ON sp.ActivityRequesterProcessId = sp2.Id LEFT JOIN app_fd_processos p ON sp.ActivityRequesterProcessId = p.id LEFT JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo LEFT JOIN shkprocessstates sps ON sps.oid = sp.State LEFT JOIN SHKActivities sact ON sact.ProcessId = sp.Id LEFT JOIN SHKActivityStates ssta ON ssta.oid = sact.State LEFT JOIN (SELECT ActivityId, group_concat(ResourceId separator '; ') AS ResourceId FROM SHKAssignmentsTable GROUP BY ActivityId) sass ON sact.Id = sass.ActivityId LEFT JOIN app_fd_atos ato ON ato.c_id_processo = p.id WHERE sps.KeyValue LIKE 'open.running' AND p.id IS NOT NULL AND (ssta.oid = '1000001' OR ssta.oid = '1000003' OR ssta.oid = '1000005') AND ato.c_status = 'AGD' AND ato.c_dataOutorga = ? AND ato.c_horaOutorga = ? AND dp.c_tipo_processo LIKE 'DL' OR dp.c_tipo_processo LIKE 'NBAOPC') AS numero_processos_esp, dp.c_n_wf_pl AS NProc, dp.c_nome_banco AS Banco, dp.c_tipo_processo AS TipoProcesso, sp.Id AS processId, sp.Name AS processName, sact.Id AS instanceId, sact.Name AS activityName, sass.ResourceId AS assignee FROM shkprocesses sp LEFT JOIN shkprocesses sp2 ON sp.ActivityRequesterProcessId = sp2.Id LEFT JOIN app_fd_processos p ON sp.ActivityRequesterProcessId = p.id LEFT JOIN app_fd_detalhe_processo dp ON dp.id = p.c_detalhe_processo LEFT JOIN shkprocessstates sps ON sps.oid = sp.State LEFT JOIN SHKActivities sact ON sact.ProcessId = sp.Id LEFT JOIN SHKActivityStates ssta ON ssta.oid = sact.State LEFT JOIN (SELECT ActivityId, group_concat(ResourceId separator '; ') AS ResourceId FROM SHKAssignmentsTable GROUP BY ActivityId) sass ON sact.Id = sass.ActivityId LEFT JOIN app_fd_atos ato ON ato.c_id_processo = p.id WHERE sps.KeyValue LIKE 'open.running' AND p.id IS NOT NULL AND (ssta.oid = '1000001' OR ssta.oid = '1000003' OR ssta.oid = '1000005') AND ato.c_status = 'AGD' AND ato.c_dataOutorga = ? AND ato.c_horaOutorga = ? GROUP BY estado, data_outorga, hora_outorga, idProc, NProc, Banco, TipoProcesso, processId, processName, instanceId, activityName, assignee) tot ON (col.c_user_colaborador = tot.assignee) WHERE c_departamentos LIKE '%PL_INT_AO%' AND numero_processos < 2 OR numero_processos_esp < 1 ORDER BY numero_processos ASC LIMIT 1;";
 stmt = con.prepareStatement(queryObterGestor);
 stmt.setObject(1, dataOutorga);
 stmt.setObject(2, horaOutorga);
 stmt.setObject(3, dataOutorga);
 stmt.setObject(4, horaOutorga);
 rs = stmt.executeQuery();
 
 if (rs.next()) {
 username = rs.getObject("c_user_colaborador");
 } else {
 username = "acompanhamento_outorgas_geral";
 }
 
 participantes.add(username);
 }
 }
 
