 try {
 if(rs != null) {
 rs.close();
 }
 if(stmt != null) {
 stmt.close();
 }
 if(con != null && !con.isClosed()) {
 con.close();
 }
 } catch(Exception e) {
 //LogUtil.error(appDef.toString(), e, "Error closing DB connection");
 LogUtil.error("AO | Mapear Validador Inicial", e, "Error closing DB connection");
 }
 }
 
