        ResultSet rs = null;
        String subtipo_value = "";
        String subtipo_label = "";
        
        AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        
        try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    //Verificar o utilizador atribuido e atualizar caso esteja errado
                    String query="SELECT c_value, c_label FROM app_fd_listas_de_valores WHERE c_nome_campo LIKE ?";
                    stmt = con.prepareStatement(query);
                    
                    StringBuilder parameterBuilder = new StringBuilder();
                    
                    if(base_processo.equals("PROC")){
                        parameterBuilder.append(" OR c_nome_campo LIKE ?");
                    }
                
                    //Here you can query from one or multiple tables using JOIN etc
                    PreparedStatement stmt = con.prepareStatement(query + parameterBuilder);
                
                    if(base_processo.equals("PROC")){
                        stmt.setString(1, "garantia");
                        stmt.setString(2, "tipo_financiamento");
                    }else if(base_processo.equals("GRT")){
                        stmt.setString(1, "tipo_financiamento");
                    }else if(base_processo.equals("TPFIN")){
                        stmt.setString(1, "garantia");
                    }else{
                        stmt.setString(1, "");
                    }
                    
                    rs = stmt.executeQuery();
                    
                    while(rs.next()){
                        subtipo_value = rs.getString(1);
                        subtipo_label = rs.getString(2);
                        
                        FormRow option = new FormRow();
                        option.setProperty(FormUtil.PROPERTY_VALUE, subtipo_value);
                        option.setProperty(FormUtil.PROPERTY_LABEL, subtipo_label);
                        rows.add(option);
                        
                    }
                }
                
        }catch(Exception e){
            LogUtil.error("HIPOTECAS - Detalhe de processo - Subtipo de processo", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("HIPOTECAS - Detalhe de processo - Subtipo de processo", e, "Error closing DB connection");
            }
            
        }
        
    }

    return rows;
}
 
