public boolean validateRecordLock(String recordLock) {
    if(recordLock != null){
        String [] rl = recordLock.split(";");
        
        if( rl.length == 2){
            String time = rl[0];
            String user = rl[1];
            String currentUser = "#currentUser.username#";
            
            DateTimeFormatter format = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss");
            LocalDateTime lockTime = LocalDateTime.parse(time, format).plusHours(1);  
            LocalDateTime currentTime = LocalDateTime.now();
            
            //Se o tempo do recordLock ainda não expirou e o utilizador for diferente 
            if(currentTime.isBefore(lockTime) && !user.trim().equals(currentUser)){
                return false;
            }
        }
        
    }
    

    return true;
}

public String calculateValidadeProposta(String dataDespacho){
    DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
    LocalDate dataValidade = LocalDate.parse(dataDespacho, dateFormatter);
    dataValidade = dataValidade.plusDays(120);
    String validade=dataValidade.format(dateFormatter);
    
    return validade;
}


public FormRowSet store(Element element, FormRowSet rows, FormData formData) {
    
    String modifiedByUser = "#currentUser.username#";
    String flag_alteracoes = rows.get(0).get("flag_alteracoes");
    String flag_alerta_alteracoes = "#form.processos_hip.flag_alerta_alteracoes#";//rows.get(0).get("flag_alerta_alteracoes");
    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    
    
    /////////////////////////   SLA   /////////////////////////////////
    
    /*********************************      É EXECUTADO QUANDO O UTILIZADOR GUARDA O FORMULÁRIO, PASSA DE TAB NO MULTIFORM OU AVANÇA PARA A PROXIMA ATIVIDADE    **********************************/
    
    //Processo é criado, SLA em estado de 'Em curso'
    //Processo fica em pendente, é 'fechado' o SLA, e criado um novo com estado de 'Pendente'
    //Processo sai de pendente, o estado passa de 'Pendente' a 'Em curso'
    
    String ProcessID = "#process.processId#";
    String idDetalheProcesso = "#form.detalhe_proce_hip.id[{form.processos_hip.detalhe_processo}]#";
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
    
    
    //Obter campos do formulário de detalhe do processo
    Map paramValues = FormUtil.loadFormData(appDef.getAppId(), appDef.getVersion().toString(), "HIPDetalheProcesso", idDetalheProcesso, false, false, false, null);
    String EstadoContratacao = paramValues.get("estado_contratacao");
    String Urgencia = paramValues.get("urgencia");
    String mainProcessId = paramValues.get("id_processo");
    
    
    /*String proximaoperativa = "#variable.proxima_operativa#";
    String operativaAnterior = "#variable.operativa_anterior#";*/
    
    boolean isLocked = false;
    
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                
                /**Caso outro utilizador que não o assignado à tarefa faça alterações no processo, não será possivel obter o id do processo**/
                //Assumindo que não existem subprocessos em paralelo
                //Obter o id do processo mais recente
                
                
                if(ProcessID.startsWith("#process.processId")){
                    
                    String queryGetProcessId = "SELECT Id FROM shkprocesses WHERE ActivityRequesterProcessId = ? order by created desc ;";
                    stmt = con.prepareStatement(queryGetProcessId);
                    stmt.setObject(1, mainProcessId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        ProcessID = rs.getObject("Id");
                    }
                }
                
                
                //Obter utilizador atribuido à tarefa
                String queryGetUserAssign = "SELECT resourceid FROM shkassignmentstable WHERE activityprocessid =?";
                stmt = con.prepareStatement(queryGetUserAssign);
                stmt.setObject(1, ProcessID);
                rs = stmt.executeQuery();
                String utilizador = "";
                if(rs.next()){
                    utilizador= rs.getObject("resourceid").toString();
                }
                
                //Obter RecorLocks de cada página
                String queryGetRecordLock = "SELECT c_record_lock FROM app_fd_processos_hip WHERE id = ?";
                
                stmt = con.prepareStatement(queryGetRecordLock);
                stmt.setObject(1, mainProcessId);
                rs = stmt.executeQuery();
                String recordLock = "";

                if(rs.next()){
                    recordLock = rs.getObject("c_record_lock").toString();;
                }
                
               
               //Se todos os record locks forem validos
                 if(!validateRecordLock(recordLock)){
                   isLocked = true;
                   
               }
               
               //Caso o utilizador que altera o processo for diferente do utilizador que está com o processo, alterar validação de alterações para não  
               if(!utilizador.equals(modifiedByUser)){
                    String queryUpdateFiltroValidacao = "UPDATE app_fd_detalhe_proce_hip SET c_validar_filtro = 'NAO' WHERE id = ? ;";
                    stmt = con.prepareStatement(queryUpdateFiltroValidacao);
                    stmt.setObject(1, idDetalheProcesso);
                    rs = stmt.executeQuery();
               }
               
               //Atualizar SubFase do Processo
                String currentSubfase = "#form.detalhe_proce_hip.subfase_processo[{form.processos_hip.detalhe_processo}]#";
                String actualSubfase = "";
                String userAssigned = "";
                String idDetalhe_processo = "#form.processos_hip.detalhe_processo#";
                
                String queryGetSubfase = "SELECT  a.`name` as subfase, asst.ResourceId as 'resourceId' FROM shkassignmentstable asst JOIN shkactivities a ON a.id=asst.activityid WHERE asst.activityprocessid =? ;";
                stmt = con.prepareStatement(queryGetSubfase);
                stmt.setObject(1, ProcessID);
                rs = stmt.executeQuery();
                
                if(rs.next()){
                    actualSubfase = rs.getObject("subfase").toString();
                    userAssigned = rs.getObject("resourceId").toString();
                    if(currentSubfase.trim().isEmpty() || currentSubfase.equals("") || !currentSubfase.equals(actualSubfase) ){
                        String queryUpdateSubfase = "Update app_fd_detalhe_proce_hip SET c_subfase_processo = ? WHERE id =? ;";
                        stmt = con.prepareStatement(queryUpdateSubfase);
                        stmt.setObject(1, actualSubfase);
                        stmt.setObject(2, idDetalhe_processo); //id registo do detalhe_processo
                        rs = stmt.executeQuery();
                    }
                }
                
                //Atualizar Gestor PL
                String queryGetGestorPL="SELECT c_gestor_pl FROM app_fd_detalhe_proce_hip WHERE id = ? ;";
                String gestorPL = "";
                stmt = con.prepareStatement(queryGetGestorPL);
                stmt.setObject(1, idDetalhe_processo);
                rs = stmt.executeQuery();
                
                if(rs.next()){
                    gestorPL = rs.getObject("c_gestor_pl").toString();
                }
                
                if(gestorPL == null || gestorPL.isEmpty() || !gestorPL.equals(userAssigned)){
                    String queryUpdateGestorPL = "UPDATE app_fd_detalhe_proce_hip SET c_gestor_pl = ? WHERE id = ?";
                    stmt = con.prepareStatement(queryUpdateGestorPL);
                    stmt.setObject(1, userAssigned);
                    stmt.setObject(2, idDetalhe_processo); //id registo do detalhe_processo
                    rs = stmt.executeQuery();
                }
                
                
                
                //Atualizar Validade da Proposta
                String validadeAtual = "#form.detalhe_proce_hip.validade_proposta[{form.processos_hip.detalhe_processo}]#";
                String dataDespacho = "#form.anal_banc_proce_hip.data_despacho[{form.processos_hip.anal_banc}]#";
                if(validadeAtual.isEmpty() || validadeAtual == ""){
                    if(!dataDespacho.isEmpty() && dataDespacho != ""){
                        calculateValidadeProposta(dataDespacho);
                    }    
                }
                
                
                //Enviar Diligência de Alterações para Validação
                String flag_validacao ="#form.processos_hip.flag_validacao#" ;//"#variable.flag_validacao#";
                FormRow row = rows.get(0);
                
                String getFlagAlertaAlteracoes = "SELECT p.c_flag_alerta_alteracoes as 'Flag' FROM app_fd_processos_hip p WHERE p.id = ?";
                    stmt = con.prepareStatement(getFlagAlertaAlteracoes);
                    stmt.setObject(1, mainProcessId);
                    rs = stmt.executeQuery();
                    if(rs.next()){
                        flag_alerta_alteracoes= rs.getObject("Flag");
                        //Se o alerta ainda não foi alançado
                        if(flag_alerta_alteracoes!=null && !flag_alerta_alteracoes.equals("1")){
                            
                            if(flag_validacao.equals("1") && flag_alteracoes.equals("SIM")){
                                
                                //Criar vaiáveis da diligência
                                String banco = "#form.banco.nome_banco[{form.processos_hip.banco}]#";
                                String complexidade = "#form.detalhe_proce_hip.complex_processo[{form.processos_hip.detalhe_processo}]#";
                                String userValidacao = "";
                                if(complexidade.equals("NOR")){
                                    userValidacao = "#variable.user_validacao#";
                                }else if(complexidade.equals("COM")){
                                    userValidacao = "#variable.user_validacao_com#";
                                }
                                
                                userValidacao= "#form.processos_hip.user_validacao#";
                                
                                
                                String faseProcesso = "#form.detalhe_proce_hip.fase_processo[{form.processos_hip.detalhe_processo}]#";
                                String equipaDest = "SISTEMA";
                                String equipaRem = "PL_INT_VAL_" + banco + "_" + complexidade + "_CRH";
                                Map variables = new HashMap();
                                variables.put("equipa_destinataria", equipaDest);
                                variables.put("gestor_remetente", userValidacao);
                                variables.put("gestor_destinatario", "SISTEMA");
                                variables.put("equipa_remetente",equipaRem );
                                variables.put("flag_alerta","1" );
                                variables.put("tipo_alerta","ALTERACOES" );
                                
                                
                                WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "Diligências");
                                WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);
                                String diligenciaId = result.getProcess().getInstanceId();
                                
                                //Obter informações da Diligência iniciada
                                String activityQuery = "select ass.activityID, ass.ResourceID  from shkprocesses pro left join shkassignmentstable ass on pro.id = ass.ActivityProcessID where pro.id = ?;";
                                stmt = con.prepareStatement(activityQuery);
                                stmt.setObject(1, diligenciaId);
                                rs = stmt.executeQuery();
            
                                if(rs.next()){
                                   
                                    //Avançar a Diligência uma fase
                                    workflowManager.assignmentForceComplete(process.getId(), diligenciaId, rs.getString(1), rs.getString(2));
                                    String nWfPl = "#form.detalhe_proce_hip.n_wf_pl[{form.processos_hip.detalhe_processo}]#";
                                    
                                    
                                    //Inserir registo da diligência na BD
                                    String assunto = "Alerta | ALTERAÇÕES | Processo - " + nWfPl;
                                    String mensagem = "Foram feitas alterações no processo " + nWfPl + " que necessitam de validação.";
                                    String insertQuery = "INSERT INTO app_fd_info_diligencia(id, c_id_processo, c_operativa_destinataria, c_equipa_destinataria, c_gestor_destinatario, c_operativa_remetente, c_equipa_remetente, c_gestor_remetente, c_assunto, c_tipo_diligencia, c_diligencia, c_urgencia, c_banco, c_complexidade, dateCreated) VALUES(?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
                                    stmt = con.prepareStatement(insertQuery);
                                    stmt.setObject(1, diligenciaId);
                                    stmt.setObject(2, mainProcessId);
                                    stmt.setObject(3, faseProcesso);
                                    stmt.setObject(4, equipaDest);
                                    stmt.setObject(5, "SISTEMA");
                                    stmt.setObject(6, faseProcesso);
                                    stmt.setObject(7, equipaRem);
                                    stmt.setObject(8, gestorPL);
                                    stmt.setObject(9, assunto);
                                    stmt.setObject(10, "ALERTA");
                                    stmt.setObject(11, mensagem);
                                    stmt.setObject(12, "SIM");
                                    stmt.setObject(13, banco);
                                    stmt.setObject(14, complexidade);
                                    stmt.setObject(15, "#date.yyyy-MM-dd hh:mm:ss#");
                                    stmt.executeQuery();
                                    
                                    //Atualizar flag para lançar o alerta
                                    row.setProperty("flag_alerta_alteracoes", "1");
                            }
                            //Atualizar valor da flag de Alterações
                            row.setProperty("flag_alteracoes", "NAO");
                        }   
                    
                    }
                }
            }
            
        }catch(Exception e){
            LogUtil.error("HIPOTECAS - PROCESSO - STORE BINDER", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("HIPOTECAS - PROCESSO - STORE BINDER", e, "Error closing DB connection");
            }
            
        }
        

    //Caso o processo não esteja bloqueado
    if(!isLocked){
        
        if(rows != null && !rows.isEmpty()) {
            PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
            FormStoreBinder binder = (FormStoreBinder) pluginManager.getPlugin("org.joget.apps.form.lib.WorkflowFormBinder");
            binder.store(element, rows, formData);
        }
        
    }else{
        formData.addFormError("HIPOTECAS - PROCESSO - Record_lock", "Campo obrigatório");
        
    }

    return rows;
}
return store(element, rows, formData);
