import java.util.Arrays;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.Form;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

 
public boolean validate(Element element, FormData formData, String[] values) {
    boolean result = true;
    
    String processId = "#form.processos.id#";
    Form form = FormUtil.findRootForm(element);
    Element atos = FormUtil.findElement("atos", form, formData);
    String idAtos = FormUtil.getElementParameterName(atos);
    
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    try{
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();
            
        if(!con.isClosed()){
        
            String queryGetMaxAlteracoes = "SELECT MAX(c_flag_alteracoes_agenda) as max FROM app_fd_atos WHERE c_id_processo = ? ;";
            stmt = con.prepareStatement(queryGetMaxAlteracoes);
            stmt.setObject(1, processId);
            rs = stmt.executeQuery();
            if(rs.next()){
                String max = rs.getObject("max");
                if(max!=null && max.equals("1")){
                    result = false;  
                    formData.addFormError(idAtos, "Houve alterações nos Atos pela Agenda. O processo vai ter de ser atualizado.");
                }
            }
        }
        
    }catch(Exception e){
            LogUtil.error("Olimpo- Validator Agendamento Alterações", e, "Error getting info from DB");
    }finally{
        try{
            if(rs != null){
            rs.close();
            }
            if(stmt != null){
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e){
            LogUtil.error("Olimpo- Validator Agendamento Alterações", e, "Error closing DB connection");
        }
        
    }
    
 
    return result;
}
 
//call validate method with injected variable
return validate(element, formData, values);
