public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {
    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
        

        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }
        
        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        String pedidoBanco = originalRow.getProperty("pedido_banco");

        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                String queryGetProcesso = "SELECT id FROM app_fd_detalhe_proce_dist WHERE c_num_pedido = ?";
                stmt = con.prepareStatement(queryGetProcesso);
                stmt.setObject(1, pedidoBanco);
                rs = stmt.executeQuery();
                if (rs.next()) {
                    LogUtil.info("Existe processo", "AAAAAAAA");
                    String idDetalhe = rs.getObject("id").toString();
                    String queryUpdate = "UPDATE app_fd_detalhe_proce_dist SET c_verificacao_banco= ? WHERE id = ?";
                    stmt = con.prepareStatement(queryUpdate);
                    stmt.setObject(1, "OLMBNC");
                    stmt.setObject(2, idDetalhe);
                    stmt.executeQuery();
                }else{
                    LogUtil.info("Não existe processo", "AAAAAAAA");
                }
            }
        }catch(Exception e){
            LogUtil.error("Distrates - Comparação de processos", e, "BeanShell Store Binder");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("Distrates - Comparação de processos", e, "BeanShell Store Binder");
            }
            
        }
      
     
    return rows;
}

//call execute method with injected variable
return storeData(element, rows, formData);
