public FormRowSet storeData(Element element, FormRowSet rows, FormData formData) {
    AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
    PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
    ExtDirectoryManager directoryManager = (ExtDirectoryManager) pluginManager.getBean("directoryManager");
    WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();

    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;

    try {

        //check for empty data
        if (rows == null || rows.isEmpty()) {
            return rows;
        }

        //get value from form to set workflow variables
        FormRow originalRow = rows.get(0);
        String scoring = originalRow.getProperty("scoring");
        String gestor = originalRow.getProperty("gestor");
        String dataEntrada = originalRow.getProperty("data_entrada");
        String viaEntrada = originalRow.getProperty("via_entrada");
        String numero_reentradas = "";
        String numeroAgenda = "";

        if (dataEntrada != null && dataEntrada.length() > 0) { // Se data estiver preenchida
            dataEntrada = dataEntrada.substring(6,10) + "-" + dataEntrada.substring(3,5) + "-" + dataEntrada.substring(0,2) + " " + dataEntrada.substring(11,16);
        } else {
            dataEntrada = "#date.yyyy-MM-dd HH:mm#"; // Caso a data não seja preenchida
        }

        int flagNumeroSequencial = 0;

        //Construção de número Agenda
        numeroAgenda = "CP" + scoring;

        /**Check if process exists**/
        boolean processExists = false;
        String processId = "";

        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

        if(!con.isClosed()) { 
            String queryGetProcessId = "SELECT p.id, dp.c_n_entrada FROM app_fd_processos_cp p JOIN app_fd_detalhe_proce_cp dp ON dp.id = p.c_detalhe_processo WHERE dp.c_n_agenda = ?";
            stmt = con.prepareStatement(queryGetProcessId);
            stmt.setObject(1, numeroAgenda);
            rs = stmt.executeQuery();

            if(rs.next()) {
                processId = rs.getObject("p.id");
                numero_reentradas = rs.getObject("dp.c_n_entrada");
                processExists = true;
            }
        }

        //Se o processo não existir
        if(!processExists) { 
            //start process with variables
            Map variables = new HashMap();
            variables.put("user_creditoPessoal", "#currentUser.username#");
            variables.put("gestor", gestor);
            numero_reentradas = Integer.parseInt(numero_reentradas) + 1 + "";

            //get process
            WorkflowProcess process = appService.getWorkflowProcessForApp(appDef.getAppId(), appDef.getVersion().toString(), "CreditoPessoal");

            //start process
            WorkflowProcessResult result = workflowManager.processStart(process.getId(), variables);

            //get process id
            processId = result.getProcess().getInstanceId();

            FormRowSet multipagedForms = new FormRowSet();
            FormRow formPrimaryKey = new FormRow();

            //definition of the fields of table processos where to store each generated primary key
            String[] fieldIds = {"detalhe_processo", "viso"};

            //definition of the formIds that correspond to the pages of the multipage form
            String[] formIds = {"detalheProcessoCreditoPessoal", "visoCreditoPessoal"};

            for(int i = 0; i < fieldIds.length; i++) { 
                String foreignKey = UuidGenerator.getInstance().getUuid();
                formPrimaryKey.setProperty(fieldIds[i], foreignKey);

                FormRowSet newRows = new FormRowSet();
                FormRow newRow = new FormRow();

                if(fieldIds[i].equals("detalhe_processo")) { 
                    newRow.put("scoring", scoring);
                    newRow.put("gestor", gestor);
                    newRow.put("data_entrada", dataEntrada);
                    newRow.put("via_entrada", viaEntrada);
                    newRow.put("n_agenda", numeroAgenda);
                    newRow.put("n_entrada",numero_reentradas + "");

                } else if (fieldIds[i].equals("viso")) {

                }

                newRow.setProperty("id_processo", processId);
                newRows.add(newRow);

                storeToOtherFormDataTable(appDef, element, newRows, formData, formIds[i], foreignKey);
            }

            formPrimaryKey.setProperty("servico", "CP");
            multipagedForms.add(formPrimaryKey);

            storeToOtherFormDataTable(appDef, element, multipagedForms, formData, "creditoPessoal", processId);

        } else {
            //Se o processo já existir

            FormRowSet newRows = new FormRowSet();
            FormRow newRow = new FormRow();

            String fase = "REE";
            String estado = "NAO"; // Comentar se quiser obter o valor do estado ao criar
            numero_reentradas = Integer.parseInt(numero_reentradas) + 1 + "";

            /*
                Descomentar se quiser obter o valor do estado ao criar

                String estado = "";
                if(originalRow.getProperty("pendente") == null || originalRow.getProperty("pendente").length() <= 0) {
                    estado = "NAO";
                } else {
                    estado = originalRow.getProperty("pendente");
                }
            */

            String dataEntrada = originalRow.getProperty("data_entrada");
            if (dataEntrada != null && dataEntrada.length() > 0){ //se data esta preenchida
                dataEntrada = dataEntrada.substring(6,10) + "-" + dataEntrada.substring(3,5) + "-" + dataEntrada.substring(0,2) + " " + dataEntrada.substring(11,16);
            }else{
                dataEntrada = "#date.yyyy-MM-dd HH:mm#";
            }

            newRow.put("fase", fase);
            newRow.put("pendente", estado);
            newRow.put("data_entrada", dataEntrada);
            newRow.put("n_entrada", numero_reentradas);
            workflowManager.processVariable(processId, "cp_estado_processo" , estado);

            newRows.add(newRow);

            String detalheProcessoPK = "#form.processos_cp.detalhe_processo["+ processId + "]#";
            detalheProcessoPK = AppUtil.processHashVariable(detalheProcessoPK, null, null, null);

            storeToOtherFormDataTable(appDef, element, newRows, formData, "detalheProcessoCreditoPessoal", detalheProcessoPK);
        }

    } catch(Exception e) {
        LogUtil.error(appDef.toString(), e, "Crédito Pessoal | Erro ao criar processo");
    } finally {
        try {
            if(rs != null) {
                rs.close();
            }
            if(stmt != null) {
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        } catch(Exception e) {
            LogUtil.error(appDef.toString(), e, "Crédito Pessoal - Criar Processo | Error closing DB connection");
        }
    }

    return rows;      
}


public void storeToOtherFormDataTable(AppDefinition appDef, Element element, FormRowSet rows, FormData formData, String formId, String id) {
    try{
        AppService appService = (AppService) AppUtil.getApplicationContext().getBean("appService");
        FormRowSet storedInfo = appService.storeFormData(appDef.getId(), appDef.getVersion().toString(), formId, rows, id);

    } catch(Exception e) {
        LogUtil.error(appDef.toString(), e, "Crédito Pessoal - Criar Processo | Error storing data");
    }
}


//call execute method with injected variable
return storeData(element, rows, formData);
