//Calcular data de fim para o SLA
public String calculateSLAD(String dataInicio, Collection feriados, String sla, String urgencia){

    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
    DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
    LocalDateTime dateInit = LocalDateTime.parse(dataInicio, formatter);  
    LocalDateTime endDateTime = null;
    String endDate = "";
    Collection holidays = feriados;



    var hour = dateInit.getHour();
    int slaNumber = Integer.parseInt(sla);


    if(urgencia.equals("SIM")){
        //Calcular SLA urgente
        endDateTime = dateInit.plusHours(slaNumber);
        //Passar dias não uteis
        while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
           endDateTime = endDateTime.plusDays(1);
        }

        endDate = endDateTime.format(formatter);

    }else{

        //Calcular SLA não urgente

            if(hour < 12){
                endDateTime = dateInit.withHour(23).withMinute(59);   /**   HORAS UTEIS???   **/

                //Incrementar dias consoante o SLA
                while(slaNumber > 1){
                    endDateTime = endDateTime.plusDays(1);
                    //Passar dias não uteis
                    while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
                       endDateTime = endDateTime.plusDays(1);
                    }   
                    slaNumber -= 1;
                }
            }else{
                endDateTime = dateInit.plusDays(1).withHour(12).withMinute(0);
                while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
                       endDateTime = endDateTime.plusDays(1);
                }

                //Incrementar dias consoante o SLA
                while(slaNumber > 1){
                    endDateTime = endDateTime.plusDays(1);
                    //Passar dias não uteis
                    while(isWeekend(endDateTime) || holidays.contains(endDateTime.format(dateFormatter))){
                       endDateTime = endDateTime.plusDays(1);
                    }
                    slaNumber -= 1;
                }
            }

        endDate = endDateTime.format(formatter);
    }

    return endDate;
}

//Calcular duração do SLA
public String calculateSLADuration(String dataInit, Collection holidays){
    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("dd/MM/yyyy HH:mm");
    DateTimeFormatter dateFormatter = DateTimeFormatter.ofPattern("yyyy-MM-dd");
    String result="";
    //if Data Init date format is not "dd/MM/yyyy HH:mm"
    if(dataInit.charAt(2) != '/'){
        //if Data Init date format is of the format "yyyy-MM-dd HH:mm"
        if(dataInit.charAt(4) == '-' && dataInit.charAt(10) == ' ' && dataInit.charAt(13) == ':'){
            dataInit =  dataInit.substring(8,10) + "/" + dataInit.substring(5,7) + "/" + dataInit.substring(0,4) + " " + dataInit.substring(11,16);
        }
    }

    LocalDateTime dateInit = LocalDateTime.parse(dataInit, formatter);
    LocalDateTime dateNow = LocalDateTime.parse("#date.dd/MM/yyyy HH:mm#",formatter);

    LocalDate dateInitDays = dateInit.toLocalDate();
    LocalDate dateNowDays = dateNow.toLocalDate();
    long days = ChronoUnit.DAYS.between(dateInitDays, dateNowDays);

    int initHours = dateInit.getHour();
    int nowHours = dateNow.getHour();
    if(initHours > 12 && nowHours < 12){
        days= days - 1;    
    }else if(days>0 && initHours < 12 && nowHours >= 12){
        days = days + 1;
    }

    long workDays = days;

    while(days>0){

        if(holidays.contains(dateInit.format(dateFormatter))){
            workDays-=1;

        }else{
            String day = dateInit.getDayOfWeek().toString();

            switch(day) {
                case "SATURDAY":{
                    workDays-=1;
                    break;
                }
                case "SUNDAY":{
                    workDays-=1;
                    break;
                }
            }
        }


        dateInit = dateInit.plusDays(1);

        days-=1;
    }

    result = "D";
    if(workDays>0){
        result =result + "+" + workDays;
    }
    return result;
}

public boolean isWeekend(LocalDateTime dt) {
    String dayOfWeek = dt.getDayOfWeek().toString();

    switch(dayOfWeek) {
        case "SATURDAY":{
            return true;
        }
        case "SUNDAY":{
            return true;
        }
        default:{
            return false;
        }
    }
}

