import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.*;
import org.joget.commons.util.LogUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import java.time.format.DateTimeFormatter;
import java.time.LocalDateTime; 
import java.time.LocalDate;
import org.joget.apps.app.model.AppDefinition;

String htmlAlertas = "";
AppDefinition appDef = AppUtil.getCurrentAppDefinition();
Connection con = null;
PreparedStatement stmt = null;
ResultSet rs = null;
ResultSet rs2 = null;

try {
//Alerta finalidadeEmprestimo
String finalidadeEmprestimo = "#form.detalhe_proce_dist.finalidade_emprestimo[{form.processos_distrates.detalhe_processo}]#";
//LogUtil.info("finalidadeEmprestimo", "FE" +  finalidadeEmprestimo);
String destinoImovel = "#form.detalhe_proce_dist.destino_imovel[{form.processos_distrates.detalhe_processo}]#";
//LogUtil.info("imovel", "DestinoImovel" +  destinoImovel);
String htmlAlertaFM= "<div class=\"container text-start\"><div class=\"row justify-content-start text-start\"><div class=\"col text-start\"><b><small class=\"text-muted m-align\"> | <span class=\"material-symbols-rounded\"> warning </span><span> || ALERTA: ";
boolean alertaFM = false;
    //if(!finalidadeEmprestimo.isEmpty() && !finalidadeEmprestimo.startsWith("#form.detalhe_proce_dist.finalidade_emprestimo[{form.processos_distrates.detalhe_processo}]#")){
    if((finalidadeEmprestimo.equals("AQS")|| finalidadeEmprestimo.equals("AQO")||finalidadeEmprestimo.equals("CNS")) && destinoImovel.equals("HPP") ){
       // LogUtil.info("dentro", "dentro" + finalidadeEmprestimo);
        alertaFM = true;
        htmlAlertaFM += "Comissão de reembolso antecipado - isento ao abrigo do DL 80-A/2022|";
        //LogUtil.info("Alerta", "Alerta" + htmlAlertaFM);
    }
    //}   
    htmlAlertaFM = htmlAlertaFM.substring(0, htmlAlertaFM.length() -1);
    htmlAlertaFM += "</span></small></b></div></div></div>";
    //LogUtil.info("AlertaFinal", "AlertaFinal" + htmlAlertaFM);
        
    if(alertaFM){
        htmlAlertas += htmlAlertaFM;
    }


}catch(Exception e){
    LogUtil.error(appDef.toString(), e, "Error getting info from DB");
}finally{
    try{
        if(rs != null){
            rs.close();
        }
        if(stmt != null){
            stmt.close();
        }
        if(con != null && !con.isClosed()) {
            con.close();
        }
    }catch(Exception e){
        LogUtil.error(appDef.toString(), e, "Error closing DB connection");
    }
}

 
FormRowSet f = new FormRowSet();
FormRow r1 = new FormRow();
//r1.put("alertas", "Alertas");

r1.put("tb_alertas", htmlAlertas);
f.add(r1);
 
return f;

