    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
Statement stmt2=null;

    try{
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();
        
       if(!con.isClosed()){
            String query = "SELECT c_formulario, c_campo, c_tabela_formulario, c_servico FROM app_fd_campos_log ORDER BY c_formulario, c_servico"; 
            String dropQuery = "";
            
            
            //Drop all log triggers
            dropTriggerQuery = "SELECT CONCAT('DROP TRIGGER IF EXISTS ', Trigger_Name, ';') as 'drop_query' FROM information_schema.TRIGGERS WHERE TRIGGER_NAME LIKE '%_logs';";
            stmt = con.prepareStatement(dropTriggerQuery);
            rs = stmt.executeQuery();
            
            
            while(rs.next()){
                dropQuery= rs.getObject("drop_query").toString();
                stmt = con.prepareStatement(dropQuery);
                stmt.executeQuery();
            }
            
            stmt2 = con.createStatement();
            
            stmt = con.prepareStatement(query);
            rs = stmt.executeQuery();
            
            String idForm = "";
            String idField = "";
            String formTable ="";
            String queryTrigger = "";
            String prevIdForm = "";
            String dropTriggerQuery = "";
            String servico = "";
            String auditLogTable = ""; 
            
            
            
            int counter = 0;
            while (rs.next()) {

                if(counter > 0){
                    prevIdForm = idForm;
                }
                
                idForm = rs.getObject("c_formulario").toString();
                idField = rs.getObject("c_campo").toString();
                formTable = rs.getObject("c_tabela_formulario").toString();
                servico = rs.getObject("c_servico").toString();
                
                //Define audit log table per servico
                if(sevico.equals("CH")){
                    auditLogTable= "app_fd_audit_log";
                }else if(servico.equals("AGN")){
                    auditLogTable= "app_fd_agn_audit_log";
                }else{
                    auditLogTable= "app_fd_audit_log";
                }
                
                
                if(counter == 0){
                    prevIdForm = idForm;
                    queryTrigger = "CREATE TRIGGER update_" + idForm + "_logs AFTER UPDATE ON app_fd_"+ formTable+" FOR EACH ROW BEGIN";
                    queryTrigger +=" DECLARE numero_proce_pl VARCHAR(255);";
                    queryTrigger += " SET numero_proce_pl := (SELECT c_n_wf_pl FROM app_fd_detalhe_processo WHERE c_id_processo LIKE old.c_id_processo);";

                }
                

                if(!prevIdForm.equals(idForm)){
                    queryTrigger += " END ";

                    //Create Trigger
                    
                    stmt2.addBatch(queryTrigger);
                    stmt2.executeBatch();
                    stmt2 = con.createStatement();

                    //Trigger for another table
                    queryTrigger = "CREATE TRIGGER update_" + idForm + "_logs AFTER UPDATE ON app_fd_"+ formTable+" FOR EACH ROW BEGIN";
                    queryTrigger +=" DECLARE numero_proce_pl VARCHAR(255);";
                    queryTrigger += " SET numero_proce_pl := (SELECT c_n_wf_pl FROM app_fd_detalhe_processo WHERE c_id_processo LIKE old.c_id_processo);";
                    queryTrigger += " IF OLD.c_" + idField +" <> NEW.c_" + idField + " THEN INSERT INTO " + auditLogTable + " (id, c_numero_proce_pl, c_process_id, c_campo, c_novo_valor, c_data_alteracao, c_utilizador) VALUES(UUID(),numero_proce_pl,old.c_id_processo, '" + idField + "', new.c_" + idField+ ", now(), old.modifiedBy); END IF;";
                    
                }else{
                    queryTrigger += " IF OLD.c_" + idField +" <> NEW.c_" + idField + " THEN INSERT INTO " + auditLogTable + " (id, c_numero_proce_pl, c_process_id, c_campo, c_novo_valor, c_data_alteracao, c_utilizador) VALUES(UUID(),numero_proce_pl,old.c_id_processo, '" + idField + "', new.c_" + idField+ ", now(), old.modifiedBy); END IF;";
                }
                
                
                
                counter += 1;
            }
            queryTrigger += " END ";
            
            //Create Trigger
            stmt2.addBatch(queryTrigger);
            stmt2.executeBatch();
 
            LogUtil.info("Gerir Campos Log", "Post Processing Tool - Campos atualizados");
            
        } else {
     
        }
    } catch(Exception e) {
            LogUtil.error("Sample app - Form 1", e, "Error loading user data in load binder");
    } finally {
            //always close the connection after used
            try {
               if(rs != null){
                    rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null) {
                    con.close();
                }
            } catch(SQLException e) {/* ignored */}
    }
