import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.app.model.AppDefinition;
import org.joget.commons.util.LogUtil;
import org.joget.directory.model.service.ExtDirectoryManager;
import org.joget.plugin.base.Plugin;
import org.joget.plugin.base.PluginManager;
import org.joget.directory.model.User;
import org.joget.workflow.model.service.WorkflowManager;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowUserManager;
import org.joget.apps.app.model.AppDefinition;
import javax.servlet.http.HttpServletRequest;
import org.joget.apps.datalist.model.DataListBinderDefaultimport;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

 
public FormRowSet load(String[] values) {
    
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    
    FormRowSet rows = new FormRowSet();
    
    FormRow option = new FormRow();
    option.setProperty(FormUtil.PROPERTY_VALUE, "");
    option.setProperty(FormUtil.PROPERTY_LABEL, "");
    rows.add(option);
    
    String value = "";
    String label = "";
    
        
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();
    
    PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
    ExtDirectoryManager directoryManager = (ExtDirectoryManager) pluginManager.getBean("directoryManager");
    
    String[] array_entidades= {"PL_EXT_ENT_A","PL_EXT_ENT_NA","PL_EXT_ENT_SA"};
    String label = "";
    
    for(i = 0; i < array_entidades.length; i++){
        
        String deptId = array_entidades[i];
        
        Collection userList = directoryManager.getUserByDepartmentId(deptId);
        for(Object u : userList) {
            User user = (User) u;
            FormRow option = new FormRow();
            String NIF = "";
            
            value = user.getUsername();
            
            try{
                DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
                con = ds.getConnection();
                
                if(!con.isClosed()){
                    String queryGetAid = "SELECT c_nif as 'NIF' FROM app_fd_info_colaboradores WHERE c_user_colaborador = ?;";
                    stmt = con.prepareStatement(queryGetAid);
                    stmt.setObject(1, value);
            		rs = stmt.executeQuery();
            		
            		if(rs.next()){
            			NIF = rs.getObject("NIF");
        		    }
        		    
                    label = user.getFirstName() + " " + user.getLastName() + " (" + NIF + ") ";

                    option.setProperty(FormUtil.PROPERTY_VALUE, value);
                    option.setProperty(FormUtil.PROPERTY_LABEL, label);
                    rows.add(option);
        		    
                }
            
            } catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error getting info from DB");
            } finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error(appDef.toString(), e, "Error closing DB connection");
                }
            }
        }
        
    }

    return rows;
}
 
//call load method with injected variable
return load(values);
