import org.joget.apps.form.service.FormUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;

public FormRowSet load(String[] values) {
    FormRowSet rows = new FormRowSet();
     
    //set groupId based on dependency value
    String id_processo = "#process.recordId#";
    String tipo_interveniente = "";
    
    if (values != null && values.length > 0) {
        tipo_interveniente = values[0];
        LogUtil.info("Beanshell import ---------", tipo_interveniente);
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                String query = "SELECT COUNT(i.c_tipo_interveniente) FROM app_fd_intervenientes_proc i  WHERE i.c_id_processo = ? AND i.c_tipo_interveniente = ?";
                stmt = con.prepareStatement(query);
                stmt.setObject(1, id_processo);
                stmt.setObject(2, tipo_interveniente);
                rs = stmt.executeQuery();
                //LogUtil.info("Beanshell import ---------", "1");
                FormRow option = new FormRow();
                while (rs.next()) {
                    LogUtil.info("Beanshell import ---------", "entra");
                    if(rs.getString(1) != null){
                        //Fiador
                        if(tipo_interveniente.equals("FIA")){
                            String opcao = "Fiador" + rs.getValue(1) + 1;
                            LogUtil.info("Beanshell import ---------", "entra - " + opcao);
                            //LogUtil.info("Beanshell documentos imoveis ---------", opcao);
                            FormRow option = new FormRow();
                            option.setProperty(FormUtil.PROPERTY_VALUE, opcao);
                            option.setProperty(FormUtil.PROPERTY_LABEL, opcao);
                            rows.add(option);  
                        }
                        
                    }
                    
                    //LogUtil.info("Beanshell import ---------", existe.toString());
                }
            }
            }catch(Exception e){
                LogUtil.error("Beanshell document - imovel", e, "Error getting info from DB");
            }finally{
                try{
                    if(rs != null){
                    rs.close();
                    }
                    if(stmt != null){
                        stmt.close();
                    }
                    if(con != null && !con.isClosed()) {
                        con.close();
                    }
                }catch(Exception e){
                    LogUtil.error("Beanshell document - imovel", e, "Error closing DB connection");
                }
                
            }
        
        //LogUtil.info("Beanshell documento imoveis ---------", rows.toString());
    }
    return rows;
}
 
//call load method with injected variable
return load(values);

