import org.joget.apps.form.model.FormData;
import org.joget.apps.form.service.FormUtil;
 
public boolean validate(Element element, FormData formData, String[] values) {
    boolean result = true;
 
    //get field 1 value from form data object
    String field1Id = "cliente_principal_count";
    Form form = FormUtil.findRootForm(element);
    Element field1 = FormUtil.findElement(field1Id, form, formData);
    
    //get field 1 value from form data object
    String field2Id = "cliente_principal_antigo";
    Form form = FormUtil.findRootForm(element);
    Element field2 = FormUtil.findElement(field2Id, form, formData);
 
    if (field1 != null && field2 != null) {
        //get value of field 1
        String[] compareValues = FormUtil.getElementPropertyValues(field1, formData);
        String[] compareValues2 = FormUtil.getElementPropertyValues(field2, formData);
        
        //compare the value of field 2 and field 1 are equals
        if (compareValues.length != 0 && !compareValues[0].isEmpty()) {
            int compare = Integer.parseInt(compareValues[0]);
            if( compare == 0 && (values == null || values[0].isEmpty()) ){
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "É necessário existir sempre um cliente principal");
                result = false;
            }else if( compareValues2.length != 0 && !compareValues2[0].isEmpty() 
                        && compare-1 == 0 && (values == null || values[0].isEmpty()) && !compareValues2[0].equals(values[0])){
                String id = FormUtil.getElementParameterName(element);
                formData.addFormError(id, "É necessário existir sempre um cliente principal");
                result = false;
            }else{
                //ignore
            }
        }
    } else {
        //ignore if the field 1 not exist
    }
 
    return result;
}
 
//call validate method with injected variable
return validate(element, formData, values);

