public boolean validate(Element element, FormRowSet rows, FormData formData) {
    boolean result = true;
    if (rows != null && !rows.isEmpty()) {
        int count = 0;
        
        //Get value from workflow variable
        String flagvalidate = "#variable.flag_ultima_atividade#";
        String faseProcessoCampo = "#form.detalhe_processo.fase_processo[{form.processos.detalhe_processo}]#";
        String enviarPara = "#form.processos.enviar_processo_hidden#";
        for (FormRow row : rows) {
            try {
                //Only validate if it is the last activity of the subflow
                //Get fase de processo through processId
                String faseProcesso = "#process.processId#";
                if(flagvalidate.equals("1")){
                    /*if(row.getProperty("resultado_checklist") == null || row.getProperty("resultado_checklist").isEmpty() || row.getProperty("resultado_checklist").equals("NOK")){
                        String id = FormUtil.getElementParameterName(element);
                        formData.addFormError(id, "Existem checklists incompletas");
                        result = false;
                    }*/

                    /*if(!faseProcesso.contains("Filtro")){
                        result = true;
                    }    */      
                    
                    if(faseProcessoCampo.equals("CNT")){
                        
                        String estadoCivil = row.getProperty("estado_civil");
                        String interveniente = row.getProperty("interveniente_soc_part");
                        String tipo_interveniente = row.getProperty("tipo_interveniente");
                        String segundo_papel = row.getProperty("segundo_papel");
                        if(segundo_papel == null){
                            segundo_papel = "";
                        }
                        
                        LogUtil.info("INTERVENIENTE:", "AAAAAAAAA" + interveniente);
                        LogUtil.info("SEGUNDO PAPEL:", "BBBBBBBBBB" + segundo_papel);
                        if(estadoCivil == null || (estadoCivil.trim().isEmpty() && interveniente.equals("particular"))){
                            
                            if(!tipo_interveniente.equals("FIA") && !tipo_interveniente.equals("PRO") && !tipo_interveniente.equals("HIP") || !segundo_papel.equals("SIM")){
       
                                LogUtil.info("Interveniente:", "CCCCCCC" + tipo_interveniente);
                                String id = FormUtil.getElementParameterName(element);
                                formData.addFormError(id, "Existem intervenientes sem estado civil");
                                result = false;
                            }
                        }
                    }else{
                         result = true;
                    }
                }
                
                //verify how many cliente_principal exists
                String isPrincipal = row.getProperty("cliente_principal");
                if (isPrincipal.equals("CHECK")){
                    count += 1;
                }
            } catch (Exception e) {}
        }
         
        if (count == 0) {
            String id = FormUtil.getElementParameterName(element);
            formData.addFormError(id, "Deve selecionar um cliente principal");
            result = false;
        }else if (count > 1){
            String id = FormUtil.getElementParameterName(element);
            formData.addFormError(id, "Selecionou mais do que um Cliente Principal, apenas pode selecionar um");
            result = false;
        }else{
            //Don't throw error
        }
    }
  
    return result;
}
  
//call validate method with injected variable
return validate(element, rows, formData);

