public FormRowSet getgridData() {
    FormRowSet fgrid = new FormRowSet();
    fgrid.setMultiRow(true);
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                
                String multiActivityIds = "#requestParam.aid#";
                String[] aid = multiActivityIds.split(";");
                
                for(int i=0; i<aid.length; i++){
                    
                String queryGetSubProcessId="SELECT processId FROM shkactivities WHERE id=?";
                stmt = con.prepareStatement(queryGetSubProcessId);
                stmt.setObject(1, aid[i]);
                rs = stmt.executeQuery();
                
                    if(rs.next()){
                        String subProcessId = rs.getObject("processId").toString();
                        if(subProcessId != null || !subProcessId.isEmpty()){
                            
                            String queryGetProcessId = "SELECT ActivityRequesterProcessId, sass.ResourceId AS 'assignee' FROM shkprocesses sp JOIN SHKActivities sact ON sact.ProcessId = sp.Id JOIN SHKAssignmentsTable sass ON sact.Id = sass.ActivityId WHERE sp.id=?  ORDER BY created DESC ;";
                            stmt = con.prepareStatement(queryGetProcessId);
                            stmt.setObject(1, subProcessId);
                            rs = stmt.executeQuery();
                            
                            if(rs.next()){
                                String processId = rs.getObject("ActivityRequesterProcessId").toString();
                                String assignee = rs.getObject("assignee").toString();
                                
                                if(processId != null || !processId.isEmpty()){
                                    
                                    String queryGetProcessDetail = "SELECT c_num_pedido as 'NEMP', c_num_workflow as 'NWF' FROM app_fd_detalhe_proce_dist WHERE c_id_processo = ?";
                                    stmt = con.prepareStatement(queryGetProcessDetail);
                                    stmt.setObject(1, processId);
                                    rs = stmt.executeQuery();
                                
                                    
                                    if(rs.next()){
                                        FormRow gr1 = new FormRow();
                                        gr1.put("gridColumn1", rs.getObject("NEMP").toString());
                                        gr1.put("gridColumn2", rs.getObject("NWF").toString());
                                        gr1.put("gridColumn3", assignee);
                                        fgrid.add(gr1);
                                       
                                    }
                                }
                                
                            }
     
                        }
       
                    }
                }
            }
  
    }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }



return fgrid;
}
return getgridData();

import org.joget.workflow.model.service.WorkflowManager;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowUserManager;
import org.joget.apps.app.model.AppDefinition;
import javax.servlet.http.HttpServletRequest;
import org.joget.commons.util.LogUtil;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.text.DecimalFormat;
import javax.sql.DataSource;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.service.FormUtil;
import org.joget.apps.form.model.*;
import org.joget.apps.form.service.*;
import java.sql.*;



public Object execute(AppDefinition appDef, HttpServletRequest request) {
    WorkflowManager wm = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    WorkflowUserManager wum = (WorkflowUserManager) AppUtil.getApplicationContext().getBean("workflowUserManager");

    //get the values from the 
    String multiActivityIds = "#requestParam.aid#";
    String[] aid = multiActivityIds.split(";");
    String username = "#form.realocar_proce_dist.user#";
    String idRealocacao = "#form.realocar_proce_dist.id#";
    String comentarios = "";




    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;

    try{
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

        if(!con.isClosed()){
            String queryGetComments = "SELECT c_comentarios_realocacao FROM app_fd_realocar_proce_dist WHERE id=?";
            stmt = con.prepareStatement(queryGetComments);
            stmt.setObject(1, idRealocacao);
            rs = stmt.executeQuery();
            if(rs.next()){
                comentarios=rs.getObject("c_comentarios_realocacao");
            }

            for(int i=0; i<aid.length; i++){

                String queryGetSubProcessId="SELECT processId FROM shkactivities WHERE id=?";
                stmt = con.prepareStatement(queryGetSubProcessId);
                stmt.setObject(1, aid[i]);
                rs = stmt.executeQuery();
                if(rs.next()){
                    String subProcessId = rs.getObject("processId").toString();
                    if(subProcessId != null || !subProcessId.isEmpty()){

                        String queryGetProcessId = "SELECT ActivityRequesterProcessId, sass.ResourceId AS 'assignee' FROM shkprocesses sp JOIN SHKActivities sact ON sact.ProcessId = sp.Id JOIN SHKAssignmentsTable sass ON sact.Id = sass.ActivityId WHERE sp.id=?  ORDER BY created DESC ;";
                        stmt = con.prepareStatement(queryGetProcessId);
                        stmt.setObject(1, subProcessId);
                        rs = stmt.executeQuery();

                        if(rs.next()){
                            String processId = rs.getObject("ActivityRequesterProcessId").toString();
                            String replaceUser = rs.getObject("assignee").toString();

                            if(processId != null || !processId.isEmpty()){

                                //getCurrentThreadUser() Method used by system to get current thread user
                                String currentUser = wum.getCurrentThreadUser();
                                wum.setCurrentThreadUser(replaceUser);
                                wum.setCurrentThreadUser(currentUser);

                                //Reassigns the assignment from a user (replaceUser) to another user (username)
                                wm.assignmentReassign("", "", aid[i], username, replaceUser);

                                //set workflow variable "approver" to the new user as well
                                 //wm.activityVariable(aid[i], "approver", username);


                                String queryUpdateRealocationComment = "UPDATE app_fd_detalhe_proce_dist SET c_comentarios_realocacao = ? WHERE c_id_processo = ?";
                                stmt = con.prepareStatement(queryUpdateRealocationComment);
                                stmt.setObject(1, comentarios);
                                stmt.setObject(2, processId); 
                                rs = stmt.executeQuery();

                            }

                        }

                    }

                }










            }
        }

     }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }

        }

    return null;
}

//call execute method with injected variable
return execute(appDef, request);
