public FormRowSet getgridData() {
    FormRowSet fgrid = new FormRowSet();
    fgrid.setMultiRow(true);
    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;
    try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                
                String multiActivityIds = "#requestParam.aid#";
                String[] aid = multiActivityIds.split(";");
                
                for(int i=0; i<aid.length; i++){
                    
                String queryGetSubProcessId="SELECT processId FROM shkactivities WHERE id=?";
                stmt = con.prepareStatement(queryGetSubProcessId);
                stmt.setObject(1, aid[i]);
                rs = stmt.executeQuery();
                
                    if(rs.next()){
                        String subProcessId = rs.getObject("processId").toString();
                        if(subProcessId != null || !subProcessId.isEmpty()){
                            
                            String queryGetProcessId = "SELECT ActivityRequesterProcessId, sass.ResourceId AS 'assignee' FROM shkprocesses sp JOIN SHKActivities sact ON sact.ProcessId = sp.Id JOIN SHKAssignmentsTable sass ON sact.Id = sass.ActivityId WHERE sp.id=?  ORDER BY created DESC ;";
                            stmt = con.prepareStatement(queryGetProcessId);
                            stmt.setObject(1, subProcessId);
                            rs = stmt.executeQuery();
                            
                            if(rs.next()){
                                String processId = rs.getObject("ActivityRequesterProcessId").toString();
                                String assignee = rs.getObject("assignee").toString();
                                
                                if(processId != null || !processId.isEmpty()){
                                    String queryGetProcessDetail;
                                    if( processId.contains("CreditoHabitacao")){
                                        queryGetProcessDetail = "SELECT c_n_wf_pl as 'NWFPL', c_n_wf_banco as 'NWFB' FROM app_fd_detalhe_processo WHERE c_id_processo = ?";
                                    }else if(processId.contains("hipotecas")){
                                        queryGetProcessDetail = "SELECT c_n_wf_pl as 'NWFPL', c_n_wf_banco as 'NWFB' FROM app_fd_detalhe_proce_hip WHERE c_id_processo = ?";
                                    }else{
                                        //do nothing
                                    }
                                    
                                    stmt = con.prepareStatement(queryGetProcessDetail);
                                    stmt.setObject(1, processId);
                                    rs = stmt.executeQuery();
                                    
                                    if(rs.next()){
                                        FormRow gr1 = new FormRow();
                                        gr1.put("gridColumn1", rs.getObject("NWFPL").toString());
                                        gr1.put("gridColumn2", rs.getObject("NWFB").toString());
                                        gr1.put("gridColumn3", assignee);
                                        fgrid.add(gr1);
                                    }
                                }
                                
                            }
     
                        }
       
                    }
                }
            }
  
    }catch(Exception e){
            LogUtil.error(appDef.toString(), e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error(appDef.toString(), e, "Error closing DB connection");
            }
            
        }



return fgrid;
}
return getgridData();

import org.joget.workflow.model.service.WorkflowManager;
