import org.joget.workflow.model.service.WorkflowManager;
import org.joget.apps.app.service.AppUtil;
import org.joget.workflow.model.service.WorkflowUserManager;
import org.joget.apps.app.model.AppDefinition;
import javax.servlet.http.HttpServletRequest;
import org.joget.commons.util.LogUtil;

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;


public Object execute(AppDefinition appDef, HttpServletRequest request) {
    WorkflowManager wm = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
    WorkflowUserManager wum = (WorkflowUserManager) AppUtil.getApplicationContext().getBean("workflowUserManager");

    //get the values from the 
    String aid = "#requestParam.aid#";
    String replaceUser = "#requestParam.replaceUser#";
    String username = "#form.realocar_processo.user#";
    String comentarios = "";
    String idProcesso="#form.realocar_processo.id_processo#";
    String idRealocacao = "#form.realocar_processo.id#";
    String subflowProcessId = "";
    String processDefId = "workflowPL#421#CHAcompanhamentoOutorgas";
    String banco = "#form.banco.nome_banco[{form.processos.banco[{form.realocar_processo.id_processo}]}]#";


    //getCurrentThreadUser() Method used by system to get current thread user
    String currentUser = wum.getCurrentThreadUser();
    wum.setCurrentThreadUser(replaceUser);
    wum.setCurrentThreadUser(currentUser);

    //Reassigns the assignment from a user (replaceUser) to another user (username)
    if(replaceUser.contains(",")){
        replaceUser = replaceUser.split(",")[0];
    }


    String variableUserContratacao = "user_contratacao_" + banco.toLowerCase();

    /*
    if(banco.equals("MTG")){
        variableUserContName += "2";
    }
    */

    wm.assignmentReassign("", "", aid, username, replaceUser);
    if(aid.contains("CHContratacao")){
        wm.activityVariable(aid,variableUserContratacao, username);    
    }

    LogUtil.info("Realocação ", "O processo " + idProcesso + " foi realocado de " + replaceUser + " para " +username);


    Connection con = null;
    PreparedStatement stmt = null;
    ResultSet rs = null;

    try{
        DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
        con = ds.getConnection();

        if(!con.isClosed()){
            String queryGetComments = "SELECT c_comentarios_realocacao FROM app_fd_realocar_processo WHERE id=?";
            stmt = con.prepareStatement(queryGetComments);
            stmt.setObject(1, idRealocacao);
            rs = stmt.executeQuery();
            if(rs.next()) {
                String queryUpdateRealocationComment;
                comentarios=rs.getObject("c_comentarios_realocacao");
                if(idProcesso.contains("CreditoHabitacao")){
                    queryUpdateRealocationComment = "UPDATE app_fd_detalhe_processo SET c_comentarios_realocacao = ? WHERE c_id_processo = ?";
                }else if( idProcesso.contains("hipotecas") ){
                    queryUpdateRealocationComment = "UPDATE app_fd_detalhe_proce_hip SET c_comentarios_realocacao = ? WHERE c_id_processo = ?";
                }else{
                    //do nothing
                }

                if(!idProcesso.contains("Procuradoria")){    
                    stmt = con.prepareStatement(queryUpdateRealocationComment);
                    stmt.setObject(1, comentarios);
                    stmt.setObject(2, idProcesso);
                    rs = stmt.executeQuery();
                }

            }

            String queryGetSubflowProcessId = "SELECT * FROM shkprocesses sp LEFT JOIN shkprocessstates sps ON sps.oid = sp.State WHERE ActivityRequesterProcessId = ? AND sps.KeyValue LIKE 'open.running';";
            stmt = con.prepareStatement(queryGetSubflowProcessId);
            stmt.setObject(1, idProcesso);
            rs = stmt.executeQuery();
            if(rs.next()) {
                subflowProcessId = rs.getObject("sp.Id");  
            }

            // Atualizar o colaborador de arquivo na BD
            if (aid.contains("Arquivo")) {
                String idToUpdate = "";

                String queryGetIdToUpdate = "SELECT id FROM app_fd_geral WHERE c_id_processo = ?";
                stmt = con.prepareStatement(queryGetIdToUpdate);
                stmt.setObject(1, idProcesso);
                rs = stmt.executeQuery();

                if(rs.next()) {
                    idToUpdate = rs.getObject("id");
                }

                String queryUpdateColaborador = "UPDATE app_fd_geral SET c_colaborador_arquivo = ? WHERE id = ?";
                stmt = con.prepareStatement(queryUpdateColaborador);
                stmt.setObject(1, username);
                stmt.setObject(2, idToUpdate);
                rs = stmt.executeQuery();
            }
        }

    }catch(Exception e) {
        LogUtil.error("REALOCAR PROCESSO", e, "Error getting info from DB");

    }finally {
        try{
            if(rs != null) {
                rs.close();
            }
            if(stmt != null) {
                stmt.close();
            }
            if(con != null && !con.isClosed()) {
                con.close();
            }
        }catch(Exception e) {
            LogUtil.error("REALOCAR PROCESSO", e, "Error closing DB connection");
        }
    }


    if (aid.contains("holdProcesses")) {

        // Store the username of the replace user in Wf Variable
        wm.processVariable(subflowProcessId, "username_distribuicao_manual", username);

        // Force the completion of the hold processes activity
        wm.assignmentForceComplete(processDefId, subflowProcessId, aid, username);
    }


    return null;
}

//call execute method with injected variable
return execute(appDef, request);
