import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.app.model.AppDefinition;
import org.joget.apps.app.service.AppService;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.service.FormUtil;
import org.joget.plugin.base.PluginManager;
import org.joget.apps.form.model.FormLoadBinder;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import javax.sql.DataSource;
import org.joget.commons.util.LogUtil;

public FormRowSet load(Element element, String primaryKey, FormData formData) {
    String formDefId = "op_registos_qual"; //change this to the form id used to store grid data
    String foreignKey = "fk"; //change this to the foreign key
    
    FormRowSet f = new FormRowSet();
    f.setMultiRow(true);
    
    // Reuse Multi Row Binder to load data
    PluginManager pluginManager = (PluginManager) FormUtil.getApplicationContext().getBean("pluginManager");
    FormLoadBinder binder = (FormLoadBinder) pluginManager.getPlugin("org.joget.plugin.enterprise.MultirowFormBinder");
     
    //Load from the grid table
    binder.setProperty("formDefId", formDefId);
    binder.setProperty("foreignKey", foreignKey);
    f = binder.load(element, primaryKey, formData);
    
    if (f == null || f.isEmpty()) {
    
        Connection con = null;
        PreparedStatement stmt = null;
        ResultSet rs = null;
        
        try{
            DataSource ds = (DataSource)AppUtil.getApplicationContext().getBean("setupDataSource");
            con = ds.getConnection();
            
            if(!con.isClosed()){
                //Build sql query with the number of inputs needed based on loop iteration
                String sql = "SELECT id, c_tipo_documento as tipoDocumento FROM app_fd_checklist_docs WHERE c_checklist = ? AND c_servico = ? ORDER BY CASE WHEN c_tipo_documento = 'Outro' THEN 1 ELSE 0 END, c_tipo_documento ASC";
                
                //Here you can query from one or multiple tables using JOIN etc
                PreparedStatement stmt = con.prepareStatement(sql);
                stmt.setObject(1, "validacaoRegisto");
                stmt.setString(2, "REG");
                
                //Execute the SELECT SQL statement
                ResultSet rs = stmt.executeQuery();
                
                //Get value from columns of record(s)
                while (rs.next()) {
                    FormRow r1 = new FormRow();
                    r1.put("tipo_documento", rs.getObject("tipoDocumento"));
                    r1.put("validacao_qual", "");
                    r1.put("motivo_qual", "");
                    f.add(r1);
                }    
            }
                
        }catch(Exception e){
            LogUtil.error("Registos", e, "Error getting info from DB");
        }finally{
            try{
                if(rs != null){
                    rs.close();
                }
                if(stmt != null){
                    stmt.close();
                }
                if(con != null && !con.isClosed()) {
                    con.close();
                }
            }catch(Exception e){
                LogUtil.error("Registos", e, "Error closing DB connection");
            }
        }
    }
    
    return f;
}

//call load method with injected variable
return load(element, primaryKey, formData);

import java.util.Arrays;
import org.joget.apps.app.service.AppUtil;
import org.joget.apps.form.model.Element;
import org.joget.apps.form.model.Form;
import org.joget.apps.form.model.FormData;
import org.joget.apps.form.model.FormRow;
import org.joget.apps.form.model.FormRowSet;
import org.joget.apps.form.service.FormUtil;
import org.joget.commons.util.LogUtil;
  
public boolean validate(Element element, FormRowSet rows, FormData formData) {
    boolean result = true;
    
    //Get value from workflow variable
    String flagValidate = "#variable.flag_ultima_atividade#";
    
    //Get fase de processo through processId
    String subfaseProcesso = "#assignment.activityName#";
    
    //Get id checklist
    String campo = FormUtil.getElementParameterName(element);
    
    //FormData Errors
    Boolean falta_validacao_qual = false;
    Boolean falta_motivo_qual = false;
    Boolean falta_solucao_qual = false;
    Boolean falta_solucao_outra_qual = false;
    
    //LogUtil.info("String", "Fase Processo " + subfaseProcesso);
    if(!subfaseProcesso.contains("Qualidade")) {
        return true;
    }
        
    //Validações das várias colunas da checklist
    if (rows != null && !rows.isEmpty()) {
        for (FormRow row : rows) {
            try {

                //Validar que nenhuma linha está vazia
                if(row.getProperty("validacao_qual") == null || row.getProperty("validacao_qual").isEmpty()){
                    String id = FormUtil.getElementParameterName(element);
                    falta_validacao_qual = true;
                    result = false;
                }
                
                //Validar que se validacao not ok motivo está preenchido
                if(row.getProperty("validacao_qual").equals("NOK") && (row.getProperty("motivo_qual") == null || row.getProperty("motivo_qual").isEmpty() || row.getProperty("motivo_qual").equals(""))){
                    String id = FormUtil.getElementParameterName(element);
                    falta_motivo_qual = true;
                    result = false;
                }
                
                //Validar que se validacao not ok solução está preenchida
                if(row.getProperty("validacao_qual").equals("NOK") && (row.getProperty("solucao_qual") == null || row.getProperty("solucao_qual").isEmpty() || row.getProperty("solucao_qual").equals(""))){
                    String id = FormUtil.getElementParameterName(element);
                    falta_solucao_qual = true;
                    result = false;
                }
                
                //Validar que se solução é outra o campo solucao outra é preenchida
                if(row.getProperty("validacao_qual").equals("NOK") && row.getProperty("solucao_qual").equals("OUT") && (row.getProperty("solucao_outra_qual") == null || row.getProperty("solucao_outra_qual").isEmpty() || row.getProperty("solucao_outra_qual").equals(""))){
                    String id = FormUtil.getElementParameterName(element);
                    falta_solucao_outra_qual = true;
                    result = false;
                }
                
                
                
            } catch (Exception e) {}
        }
    }
    
    //Escrever mensagem de erro de validação no form
    if(falta_validacao_qual){
        formData.addFormError(campo, "Validações em falta");
    }
    
    if(falta_motivo_qual){
        formData.addFormError(campo, "Motivo em falta");
    }

    if(falta_solucao_qual){
        formData.addFormError(campo, "Solução em falta");
    }
    
    if(falta_solucao_outra_qual){
       formData.addFormError(campo, "Solução Outra em falta"); 
    }
  
    return result;
}
  
//call validate method with injected variable
return validate(element, rows, formData);
