import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import org.joget.commons.util.LogUtil;


public void downloadFile() {

   String fileUrl = "https://www.metrolisboa.pt/wp-content/uploads/2022/06/Metropolitano-De_Lisboa_Diagrama-da-Rede_maio2022.pdf";
   try {
       URL url = new URL(fileUrl);
       HttpURLConnection httpURLConnection = (HttpURLConnection) url.openConnection();
       int responseCode = httpURLConnection.getResponseCode();
       if (responseCode == HttpURLConnection.HTTP_OK) {
           InputStream inputStream = httpURLConnection.getInputStream();
           // Crie um OutputStream para escrever os dados no cliente (resposta)
           OutputStream outputStream = new BufferedOutputStream(new FileOutputStream("D:/Users/rgoncalves/Downloads"));
           // Leia os dados do InputStream e escreva no OutputStream
           byte[] buffer = new byte[1024];
           int bytesRead;
           while ((bytesRead = inputStream.read(buffer)) != -1) {
               outputStream.write(buffer, 0, bytesRead);
           }
           // Feche os fluxos
           inputStream.close();
           outputStream.close();
           System.out.println("Download e envio para o cliente concluídos com sucesso.");
       } else {
           System.out.println("Falha na conexão. Código de resposta: " + responseCode);
       }
   } catch (IOException e) {
       e.printStackTrace();
   }
}

downloadFile();
