/*Mudar o CSS das mensagens de erros de validação*/
$(document).ready(function(){
    /*Para cada mensagem de erro*/
   $("span.form-error-message").each(
        function(){
            var message =$(this).text().toUpperCase();
            if($(this).parent().parent().find("input").length !== 0){
                /*se for um campo de texto*/
                if($(this).parent().parent()[0].childNodes[3].nodeName == "INPUT"){
                    $(this).parent().parent().find("input").css("border", "1px solid red").attr("placeholder", message).css("color", "red!important").addClass('redplaceholder');
                    $(this).hide();
                /*se for um radio button*/
                }else if($(this).parent().parent()[0].nodeName == "DIV"){
                    /*Por implmenta*/
                }
            }
            /*se for uma select box*/
            if($(this).parent().parent().find("select").length !== 0){
                $(this).parent().parent().find("select").css("border", "1px solid red").append('<option selected value="whatever">'+ message +'</option>').css("color", "red");
                $(this).hide();
            }
        }
    );

});


